INSERT INTO `activities` VALUES ('1', 'Đàn ca tài tử', 'Là một loại hình nghệ thuật của đàn và ca, do những người bình dân, thanh niên nam nữ nông thôn Nam Bộ hát ca sau những giờ lao động', '1', '');
INSERT INTO `activities` VALUES ('2', 'Tát mương bắt cá', 'Ai cũng xuống mương bắt cá nên dù nước ngang đầu gối, những chú cá lóc, cá trê béo tròn cũng không thoát khỏi những bàn tay \"săn mồi\", khiến tiếng cười vang khắp vườn.', '1', '');
INSERT INTO `activities` VALUES ('3', 'Câu cá giăng lưới giải trí', 'Hiện nay, đa số khách du lịch trong và ngoài nước rất yêu thích cảnh quan thiên nhiên, khám phá và trải nghiệm các hoạt động trong cuộc sống dân dã. Vì vậy, câu cá giải trí trở thành một trong những dịch vụ hút khách.', '1', '');
INSERT INTO `activities` VALUES ('4', 'Tham quan vườn trái cây', 'Bạn sẽ được đi vào vườn trái cây để tham quan, được tự tay lựa chọn những quả chín và thưởng thức ngay tại vườn.', '1', '');
INSERT INTO `activities` VALUES ('5', 'Bơi xuồng trên sông', 'Bạn sẽ được tự tay chèo thuyền, tự mình trải nghiệm cái hoạt động rất đỗi bình dân của người dân Nam Bộ.', '1', '');
INSERT INTO `activities` VALUES ('6', 'Đu dây thừng qua sông', 'Là một trải nghiệm không thể thiếu khi du lịch Bến Tre', '1', '');
INSERT INTO `activities` VALUES ('7', 'Tham quan cơ sở làm kẹo dừa', 'Hiện nay, kẹo dừa Bến tre là một đặc sản nổi tiếng của cả nước, mang đậm nét văn hóa của xứ sở dừa.', '1', '');
INSERT INTO `activities` VALUES ('8', 'Tắm biển', 'Dù không mang vẻ đẹp của những bài biển đầy cát trắng nhưng biển cồn Bửng của xứ dừa vẫn là điểm đến của nhiều trái tim yêu thích sự bình yên, dân dã của vùng đất mới.', '1', '');
INSERT INTO `activities` VALUES ('9', 'Chạy xe đạp trên cầu hẹp', 'Du khách từ nhiều nơi thích thú đổ về các khu du lịch sinh thai ở Bến Tre để trải nghiệm trò đạp xe qua cầu rộng 20cm được ví như \"cầu khỉ\" đầy mạo hiểm.', '1', '');
INSERT INTO `activities` VALUES ('10', 'Bóng nước', 'Với những quả bóng đầy màu sắc được bơm căng hơi, người chơi sẽ chui vào đó rồi thả mình bồng bềnh trên mặt nước.', '1', '');
INSERT INTO `activities` VALUES ('11', 'Trượt máng ở Núi Bà', 'Hệ thống máng trượt khép kín ở núi Bà sẽ mang đến cho du khách những trải nghiệm khác nhau về ngọn núi này.', '1', '');
INSERT INTO `activities` VALUES ('12', 'Cắm trại', '', '1', '');
INSERT INTO `activities` VALUES ('13', 'Leo núi', '', '1', '');
INSERT INTO `activities` VALUES ('14', 'Trải nghiệm các trò chơi trên nước', 'Bạn có thể tham gia vào hoạt động trải nghiệm của người dân là theo thuyền ra hồ đánh bắt cá hoặc tham gia các trò chơi trên nước như thuyền buồm, xuồng máy tốc độ cao.', '1', '');
INSERT INTO `activities` VALUES ('15', 'Hành hương', '', '1', '');
INSERT INTO `activities` VALUES ('16', 'Leo Phanxipan', 'Chinh phục nóc nhà Đông Dương là ước mơ của rất nhiều người trẻ. Nếu thích thử thách và chiến thắng bản thân mình thì còn ngại ngần gì nữa mà không lên đường. ', '1', '');
INSERT INTO `activities` VALUES ('17', 'Ngắm những thửa ruộng bậc thang kỳ vĩ nhất thế giới', 'Ruộng bậc thang ở Sa Pa được vinh danh là một trong những thửa ruộng bậc thang kì vĩ nhất thế giới. Thiên nhiên không chỉ ban tặng cho người dân ở vùng đất này cảnh quan và khí hậu lý tưởng. ', '1', '');
INSERT INTO `activities` VALUES ('18', 'Leo Hàm Rồng, ngắm thị trấn Sa Pa từ trên cao', 'Quan sát thị trấn từ chòi cao 1800m trên đỉnh Hàm Rồng là một trải nghiệm thú vị mà bất cứ ai đến Sa Pa cũng nên thử. Thị trấn ở một góc nhìn khác.', '1', '');
INSERT INTO `activities` VALUES ('19', 'Ngắm bình minh, hoàng hôn Sa Pa', 'Những cơn gió sáng sớm se lạnh sẽ làm bạn khẽ rùng mình. Ôm trong tay ly café ấm, đĩa trứng ốp la bên cạnh, nhìn ra ô sổ tại một khách sạn có góc nhìn đẹp. Bạn sẽ gặp một Sa Pa nhẹ nhàng trong sương sớm.', '1', '');
INSERT INTO `activities` VALUES ('20', 'Đi bộ, sống chậm ở Sa Pa', 'Du lịch đi bộ ở Sa Pa sẽ mang lại cho bạn những trải nghiệm không thể quên. Sapa là một trong những địa điểm du lịch đi bộ dài nhất trên thế giới.', '1', '');
INSERT INTO `activities` VALUES ('21', 'Ngủ homestay tại nhà một người dân tộc thiểu số', 'Đến Sa Pa, ngủ homestay trong nhà của một người dân tộc thiểu số. Ăn những món ăn dân giã đặc trưng, mới lạ. Thức giậy trong bản làng xa trung tâm thị trấn sẽ là một trải nghiệm tuyệt vời mà bạn không thể quên được khi đến với mảnh đất này.', '1', '');
INSERT INTO `activities` VALUES ('22', 'Tới thác Bạc và thác Tình Yêu', 'Đến thăm hai con thác này bạn sẽ không chỉ thấy sảng khoải khi tận hướng hơi nước mát lạnh. Mà còn được nghe câu chuyện truyền thuyết về thác Tình Yêu, nơi nàng tiên nữ thứ bảy nhà trời gặp chàng tiều phu. ', '1', '');
INSERT INTO `activities` VALUES ('23', 'Ngắm cảnh quan hùng vỹ nơi đỉnh Ô Quy Hồ', 'Cùng nằm trên đường đi Lai Châu, qua thác Bạc và thác Tình Yêu bạn sẽ được ghé thăm đỉnh đèo Ô Qui Hồ. Một trong tứ đại đèo của núi rừng phía Bắc. Đứng trên đỉnh đèo bạn sẽ nhìn thấy những con đường uống lượn nối liền hai tỉnh Lào Cai và Lai Châu. Cạnh đó', '1', '');
INSERT INTO `activities` VALUES ('24', 'Thưởng thức đặc sản Sa Pa', 'Sẽ thật đáng tiếc nếu bạn bỏ lỡ những món đặc sản hấp dẫn như: mầm đá, cá hồi –cá suối, lợn bản… cùng những hàng quán đồ nướng thơm phức vào ban đêm ở trung tâm thị trấn.', '1', '');
INSERT INTO `activities` VALUES ('25', 'Khám phá một Sa Pa không khách du lịch', 'Nếu leo Fansipan, đi bộ quanh trung tâm thị trấn và các bản làng du lịch không đem lại cho bạn cảm giác mới lạ khi đến Sa Pa.', '1', '');
INSERT INTO `activities` VALUES ('26', 'Thả diều ở Mũi Né', 'Thả diều tại Mũi Né là hoạt động được nhiều người lựa chọn, tại đây du khách có thể mượn hoặc thuê diều nhiều hình dạng khác nhau để thả, có từng loại cho người lớn hoặc trẻ nhỏ.', '1', '');
INSERT INTO `activities` VALUES ('27', 'Tham quan công viên tượng cát', 'Nổi tiếng với những bãi cát ngút ngàn nên khi du lịch Phan Thiết đừng quên ghé thăm công viên tượng cát Forgotten Land để chiêm ngưỡng những tác phẩm điêu khắc từ cát và nước của các nghệ sĩ điêu khắc đến từ 12 quốc gia.', '1', '');
INSERT INTO `activities` VALUES ('28', 'Câu mực đêm', 'Những món ăn từ mực tại Phan Thiết cũng là ấn tượng khó quên trong lòng du khách vì vậy hoạt động câu mực đêm là một trải nghiệm không thể thiếu trong hành trình khám phá vùng đất này.', '1', '');
INSERT INTO `activities` VALUES ('29', 'Đua xe địa hình', 'Khu đồi cát là địa điểm lý tưởng để trải nghiệm đua môtô địa hình 4 bánh, vượt qua nhiều con dốc cát. Nơi đây thu hút đông khách du lịch, đặc biệt là những người trẻ mê khám phá, mạo hiểm.', '1', '');
INSERT INTO `activities` VALUES ('30', 'Đi chợ làng chài', 'Bạn cần thức dậy sớm để đi chợ vì thuyền hải sản của ngư dân về lúc 4, 5h sáng. Cá, mực, tôm, cua… tươi rói được bày bán ngay tại bãi biển là điểm thu hút du khách của các chợ làng chài Mũi Né, Phú Thủy.', '1', '');
INSERT INTO `activities` VALUES ('31', 'Trượt zipline và chinh phục ngọn cây với trò highwire', 'Bằng cách treo người trên những sợi cáp dài và trượt dài từ đỉnh núi xuống chân núi, bạn sẽ trải qua cảm giác cực kỳ thú vị khi nhìn ngắm khung cảnh từ trên cao vô cùng hấp dẫn.', '1', '');
INSERT INTO `activities` VALUES ('32', 'Tắm suối khoáng nóng', ' Nguồn suối khoáng đặc biệt ở chỗ nhiệt độ nóng từ 30 độ và lên tới 60 độ C khi ở càng gần mạch nguồn. Bên cạnh đó, cũng có những trò chơi như ống trượt hay máng trượt tại khu công viên nước được các bạn trẻ thích thú.', '1', '');
INSERT INTO `activities` VALUES ('33', 'Nghe ca Huế trên sông Hương', 'Ngồi trên thuyền khoảng 1 tiếng đồng hồ, nghe hát mộc, không míc, thả cái đèn hoa đăng nho nhỏ trên dòng Hương làm cho buổi tối ở Huế thêm thú vị.', '1', '');
INSERT INTO `activities` VALUES ('34', 'Thăm Đại Nội, lăng tẩm', 'Thăm Đại Nội và lăng tẩm dường như là một phần không thể thiếu khi đến du lịch Huế, bởi lẽ, những dấu tích của một triều đại lâu đời trong lịch sử Việt Nam được thể hiện rõ ràng nhất trong chính đền đài lăng tẩm tại mảnh đất này.', '1', '');
INSERT INTO `activities` VALUES ('35', 'Khám phá ẩm thực Huế', 'Có hàng chục loại bánh ngon lành mà bạn nên thử, nào là bánh khoái giòn giòn vàng ươm như chiếc bánh xèo nho nhỏ, bánh bèo chén mỏng nhẹ trên khay với tôm chấy và tóp mỡ, bánh lọc xinh xinh nổi bật với màu cam đỏ của con tôm và mỡ trưng, hay bánh nậm mỏng', '1', '');
INSERT INTO `activities` VALUES ('36', 'Bà Nà Hills Fantasy Park', 'Dựa theo cuốn tiểu thuyết “Hành trình vào trung tâm trái đất” và “Hai vạn dăm dưới biển” của nhà văn người Pháp Jules Verne, Bà Nà Hills Mountain Resort đã cho ra đời khu vui chơi giải trí trong nhà với diện tích 21.000m2 mang đă', '1', '');
INSERT INTO `activities` VALUES ('37', 'Tham quan Bão tàng Sáp', 'Khu trưng bày tượng sáp Bà Nà Hills - Một trong những bảo tàng tượng sáp lớn trên thế giới cũng là khu trưng bày lớn nhất và duy nhất tại Việt Nam, là địa điểm tham quan thú vị không thể bỏ qua khi du khách đến với Khu Du Lịch Bà Nà Hills Mountain Resort.', '1', '');
INSERT INTO `activities` VALUES ('38', 'Trải nghiệm Cáp treo Bà Nà-Núi Chúa', 'Toàn bộ tuyến cáp treo bao gồm 22 trụ, 94 cabin, công suất 1500 khách/giờ, vận tốc trung bình 6m/s. Hiện nay Cáp treo Bà Nà đã đạt 4 kỉ lục được thế giới công nhận: kỉ lục về tuyến cáo treo một dây dài nhất, cáp treo có độ chênh lớn nhất, cáp treo có tổng', '1', '');
INSERT INTO `activities` VALUES ('39', 'Chơi Tàu hỏa leo núi', 'Trước đây, khi muốn tham quan vườn hoa Le Jardin D’Amour, hầm rượu Debay hay vườn Tịnh Tâm, quý khách phải phải xếp hàng, tốn khá nhiều thời gian. Tuy nhiên, với dịch vụ  “Tàu hỏa leo núi” hiện tại thì vấn đề này đã được khắc phục hoàn toàn. Không chỉ tiế', '1', '');
INSERT INTO `activities` VALUES ('40', 'Khu xe trượt ống', 'Bạn là người đam mê tốc độ và muốn tìm cảm giác mạnh trên cao? Hãy thử điều khiển xe trượt ống len lỏi qua những cung đường xoắn ốc đầy thách thức. Được thiết kế an toàn tuyệt đối cùng 2 vòng xoáy kép duy nhất tại Việt Nam, trò chơi này là lựa chọn hàng đ', '1', '');
INSERT INTO `activities` VALUES ('41', 'Tham quan Hầm rượu', 'Tọa lạc ngay trước tượng đức Phật Thích Ca Mâu Ni, được xây dựng vào năm 1923, hầm rượu cổ vẫn được bảo quản trong điền kiện rất tốt và trở thành điểm đến không thể bỏ qua của mọi du khách khi đến với Bà Nà. Nơi đây cất giữ các loại rượu ngon và hảo hạng ', '1', '');
INSERT INTO `activities` VALUES ('42', 'Vườn hoa LE JARDIN D’AMOUR\n', 'Được mở cửa từ tháng tư năm 2014, Vườn Hoa Tình Yêu điểm tô thêm vào cảnh sắc thiên nhiên tuyệt vời của khu du lịch Bà Nà, mang đến những giây phút bình yên và lãng mạn trong khung cảnh thiên nhiên đẹp tựa trong tranh.\n\nVới tổng cộng 7 héc-ta bao gồm Quản', '1', '');
INSERT INTO `activities` VALUES ('43', 'Tham quan Khu du lịch Tâm linh', 'Ở Bà Nà Hill, bạn có thể tìm thấy những biểu tượng mang tính tâm linh như chùa chiền, đền đài hay tượng các đức Phật. Chắc hẳn sẽ là điểm dừng chân cho những ai mong cầu bình an và sức khỏe cho gia đình.', '1', '');
INSERT INTO `activities` VALUES ('44', 'Tham quan Khu làng ẩm thực', 'Mang đến bạn các lựa chọn ẩm thực phong phú và độc đáo với nhiều nhà hàng khác nhau và các loại thực đơn chọn lọc – thực đơn gọi món, thực đơn nướng, thức ăn nhanh – sẽ chắc chắn làm hài lòng bạn.\n\nHầu hết các nhà hàng nằm tại Quảng Trường Du Dôme, ngay p', '1', '');
INSERT INTO `activities` VALUES ('45', 'Tắm biển tại các bãi tắm', 'Nếu ai du lịch đến Đà Nẵng đều muốn một lần tắm biển trên bãi biển Mỹ Khê. Vài các buổi chiều mùa hè tại bãi biển thường tập trung rất nhiều người hóng mát và tắm biển. Có 3 khu vực chính bãi tắm phục vụ du khách tại biển Mỹ Khê.\nNgay ngã ba đường Phạm Vă', '1', '');
INSERT INTO `activities` VALUES ('46', 'Chơi dù lượn', '', '1', '');
INSERT INTO `activities` VALUES ('47', 'Ngắm sông về đêm', '', '1', '');
INSERT INTO `activities` VALUES ('48', 'Ăn uống vui chơi khu vực ven sông', '', '1', '');
INSERT INTO `activities` VALUES ('49', 'Xem động vật hoang dã ban đêm', 'Tuỳ thuộc vào điều kiện thời tiết, Vườn quốc gia có thể tổ chức chương trình xem động vật hoang dã ban đêm, thực hiện chương trình này du khách sẽ có cơ hội được nhìn thấy một số loài động vật hoang dã như: Sóc đen, Sóc bay, Hoẵng, Culi và một số loài thú', '1', '');
INSERT INTO `activities` VALUES ('50', 'Xem chim', 'Cúc Phương là một trong những điểm đa dạng nhất về khu hệ chim ở miền Bắc Việt Nam, với 308 loài đã được phát hiện và thống kê, trong đó có nhiều loài quý hiếm như: Gõ kiến đầu đỏ, Gà lôi trắng, Niệc nâu, Đuôi cụt bụng vằn…, đặc biệt có nhiều là loài đặc ', '1', '');
INSERT INTO `activities` VALUES ('51', 'Đạp xe trong rừng\n', 'Một trong những hình thức khám phá bí ẩn của thiên nhiên Cúc Phương đó là đạp xe đạp trong rừng. Đạp xe trong rừng sẽ mang lại cho du khách không chỉ những cảm giác yên tĩnh, cảm nhận thực sự về thiên nhiên mà còn giúp cho du khách có được những cơ hội để', '1', '');
INSERT INTO `activities` VALUES ('52', 'Quan sát các loại bò sát, lưỡng cư, côn trùng', 'Cúc Phương là điểm đa dạng về bò sát, lưỡng cư và côn trùng. Hiện tại Vườn quốc gia đã điều tra và thống kê được 110 loài bò sát và lưỡng cư, 1899 loài côn trùng, trong đó có nhiều loài là loài đặc hữu của Cúc Phương và Việt Nam. Một số loài dễ bắt gặp và', '1', '');
INSERT INTO `activities` VALUES ('53', 'Thăm các điểm đa dạng sinh học\n', 'Hiện tại Cúc Phương đã thống kê được 43 điểm đa dạng sinh học, đây là kết quả từ sự hợp tác nghiên cứu giữa Vườn quốc gia Cúc Phương và nhóm hợp tác nghiên cứu đa dạng sinh học quốc tế (ICBG), thăm các điểm đa dạng sinh học này du khách sẽ có nhiều cơ hội', '1', '');
INSERT INTO `activities` VALUES ('54', 'Chương trình văn nghệ dân tộc\n', 'Đến với Cúc Phương, ngoài cơ hội chiêm ngưỡng những vẻ đẹp mà tạo hoá ban tặng, du khách còn được thưởng thức những điệu múa, những bài hát truyền thống của dân tộc Mường và các dân tộc vùng cao, những chàng trai, cô gái Mường trong trang phục truyền thốn', '1', '');
INSERT INTO `activities` VALUES ('55', 'bơi thuyền Kayak', 'Nghỉ lại tại Cúc Phương, du khách có thể tham gia nhiều hoạt động giải trí, thư giãn. Chèo thuyền kayak trên hồ Mạc và hồ Yên Quang là một trong những hoạt động hấp dẫn được nhiều du khách ưa thích.', '1', '');
INSERT INTO `activities` VALUES ('56', 'chèo thuyền ngắm cảnh', '', '1', '');
INSERT INTO `activities` VALUES ('57', 'Tham quan khu chùa Bái Đính mới', 'Trong những ngày lễ hội, chùa Bái Đính thường rất đông khách và nhộn nhịp. Một số điểm tham quan chính gồm cổng Tam Quan, tháp chuông, các điện Quan Âm, Pháp Chủ, Tam Thế. Trong đó:\n\nCổng Tam quan: Nơi đây được bố trí hai tượng Hộ pháp (ông thiện và ác) b', '1', '');
INSERT INTO `activities` VALUES ('58', 'Tham quan khu chùa Bái Đính cổ', 'Cách điện Tam Thế của khu chùa mới khoảng 800 m về phía nam, chùa Bái Đính cổ nằm gần đỉnh một rừng núi khá yên tĩnh, gồm một nhà tiền đường ở giữa. Bên phải là hang sáng thờ Phật, sau đó tới đền thờ thần Cao Sơn ở sát cuối cửa sau. Bên trái là đền thờ th', '1', '');
INSERT INTO `activities` VALUES ('59', 'Thưởng thức món ăn', 'Bạn có thể dừng chân nghỉ trưa và dùng bữa trong một nhà hàng ngay tại khuôn viên chùa Bái Đính. Các món nơi đây đa dạng gồm bánh bao, cháo đậu xanh, đùi gà, phở bò, cơm… nhưng nguyên liệu chay hoàn toàn. Giá cả chỉ dao động trong khoảng 5.000-25.000 đồng', '1', '');
INSERT INTO `activities` VALUES ('60', 'Ngồi xích lô\n', 'Từng là phương tiện đi lại phổ biến trong thế kỷ trước, hiện xích lô chỉ còn là nét văn hóa truyền thống Hà Nội và đa phần phục vụ du khách quốc tế. Trên những con phố thủ đô, bóng dáng loại phương tiện này cũng không xuất hiện nhiều. Với cuộc sống hối hả', '1', '');
INSERT INTO `activities` VALUES ('61', 'uống bia phố Tạ Hiện buổi tối\n', 'Người Hà Nội mê bia và cũng dành một tình cảm không nhỏ cho bia vỉa hè. Nhưng văn hóa uống bia đất Hà thành đặc biệt ở chỗ có hẳn một “phố bia” nổi tiếng- Tạ Hiện ', '1', '');
INSERT INTO `activities` VALUES ('62', 'Thưởng thức đặc sản Hà Nội', '', '1', '');
INSERT INTO `activities` VALUES ('63', 'Ngắm cảnh Hồ Tây hoàng hôn xuống', '', '1', '');
INSERT INTO `activities` VALUES ('64', 'Đi chơi buổi tối trong chuyến du lịch Sài Gòn\n\n', 'QUÁN BAR\nGIẢI TRÍ: XEM PHIM, XEM KICH', '1', '');
INSERT INTO `activities` VALUES ('65', 'Mua sắm Sài Gòn:\nChợ Bến Thành\nSaigon Square 1&2, Taka Plaza', 'Đây là địa chỉ mua sắm ưa thích của rất nhiều nữ công dân Sài Gòn. Với thế mạnh về quần áo, giày dép, túi xách, hàng bình dân và trung cấp, Sài Gòn Square thường xuyên tấp nập khách hàng, đặc biệt vào cuối tuần.\nCHỢ BÌNH TÂY\nDIAMOND PLAZA, PARKSON, VINCOM', '1', '');
INSERT INTO `activities` VALUES ('66', 'Tham quan cực Nam tổ quốc', '', '1', '');
INSERT INTO `activities` VALUES ('67', 'Chợ đêm Cà Mau', '', '1', '');
INSERT INTO `activities` VALUES ('68', 'Tham quan, ăn uống, mua sắm', 'Chợ Cái Răng thường họp khá sớm, thường từ lúc mờ sáng và đến khoảng 8, 9 giờ thì vãn. Những loại hàng hóa được bày bán trên ghe, thường thì mỗi ghe sẽ chuyên bán một loại mặt hàng. Du khách có thể mua các loại trái cây để thưởng thức hoặc làm quà. Du khá', '1', '');
INSERT INTO `activities` VALUES ('69', 'Đi bộ dọc bến Ninh Kiều', 'từ công viên bến Ninh Kiều du khách có thể nhìn thấy cầu Cần Thơ bề thế, nhìn sang là Xóm Chài mộc mạc và một dải cù lao xanh mướt. Nằm cạnh công viên là khu Chợ Nhà Lồng cổ Cần Thơ; và cảng Cần Thơ hiện đại.', '1', '');
INSERT INTO `activities` VALUES ('70', 'Tắm biển', 'Tại đây có các trò chơi dưới nước hấp dẫn như: moto nước, chèo thuyền kayak, phao chuối…đặc biệt thuê cao tốc chạy dọc bãi biển được nhiều du khách lựa chọn khi đến đây. Buổi chiều, nhiều phụ huynh đưa con em đến để tập bơi trong môi trường tự nhiên, vừa ', '1', '');
INSERT INTO `activities` VALUES ('71', 'Cúng, cầu nguyện', 'Đến thiền viện Trúc Lâm Phương Nam thưởng ngoạn khung cảnh và cầu an sẽ là chuyến hành hương đáng nhớ của bạn trong những ngày đầu năm mới. Bởi lẽ, các giá trị văn hóa, tâm linh thấm đẫm trong từng góc nhỏ của thiền viện chắc chắn sẽ mang lại cho du khách', '1', '');
INSERT INTO `activities` VALUES ('72', 'Xem và chơi chơi 1 vài trò chơi', 'các hoạt động như đua chó, đua heo, một ngày làm điền chủ, tát mương bắt cá, bơi thuyền, câu cá, tham quan làng nghề truyền thống, nghe đàn ca tài tử, xiếc khỉ…', '1', '');
INSERT INTO `activities` VALUES ('73', 'Tắm biển, ngắm hoàng hôn', 'Vào buổi bình minh và hoàng hôn,\n mặt trời đỏ rực như tan vào nước biển mênh mông. Bãi Trước như một nửa vành trăng tựa lưng vào đất liền, hai đầu là hai ngọn núi Tương Kỳ và Tao Phùng. Thiên nhiên sơn thuỷ hửu tình đã tạo cho Bãi Trước cảnh thơ mộng là b', '1', '');
INSERT INTO `activities` VALUES ('74', 'Tham quan, chiêm bái', 'Thích Ca Phật đài là một danh lam thắng cảnh nổi tiếng bậc nhất ở Vũng Tàu và miền Nam. Hằng năm, nơi đây tiếp đón cả triệu lượt du khách đến tham quan, chiêm bái.', '1', '');
INSERT INTO `activities` VALUES ('75', ' cáp treo Vũng Tàu', 'Là hệ thống Cáp Treo đầu tiên của tỉnh Bà Rịa - Vũng Tàu, được nhập từ châu Âu với công suất lớn nhất Việt Nam hiện nay, 2.000 người/giờ. Cáp Treo có tổng chiều dài hơn 500m, đưa du khách từ Nhà ga Cáp Treo số 1 lên đỉnh Núi Lớn, có độ cao 210 mét so với ', '1', '');
INSERT INTO `activities` VALUES ('76', 'Thăm các làng chài trên \nVịnh', 'Rời xa những ánh điện lung linh, xa hoa của các trung tâm thành phố, rời bỏ khỏi cuộc sống xô bồ của chốn thành thị. Các làng chài ven biển ở Hạ Long thật yên bình, trong lành, là điểm đến, điểm dừng chân lý thú cho các du khách. Điểm đến cho hoạt động tr', '1', '');
INSERT INTO `activities` VALUES ('77', 'Khám phá những hang \nđộng tuyệt đẹp', 'Vịnh Hạ Long được nhiều du\n khách ví như bức tranh thủy mặc của thiên nhiên, được tạo hóa ban cho một vẻ đẹp hoàn hảo. Và ở bức tranh thủy mặc đó, ngoài “nước và đá” như nhiều người thường nói, thì nơi đây còn nổi tiếng với những hang động đẹp và độc đáo.', '1', '');
INSERT INTO `activities` VALUES ('78', 'Thử sức với bộ môn leo núi', 'Không chỉ hấp dẫn bởi vẻ \nđẹp non nước hữu tình, biển trời thơ mộng, Vịnh Hạ Long với địa hình đa dạng và hiểm trở còn là một thách thức đối với những du khách yêu thích leo núi. Nếu bạn là một tín đồ của môn thể thao mạo hiểm này, chắc hẳn những mỏm đá g', '1', '');
INSERT INTO `activities` VALUES ('79', 'Ngắm Vịnh Hạ Long \ntừ thủy phi cơ', 'Nếu bạn đã từng trải nghiệm \nthưởng ngoạn khung cảnh Vịnh Hạ Long trên những chuyến du thuyền mà vẫn chưa thỏa mãn, vì chưa ngắm được những góc nhìn đẹp về Vịnh Hạ Long như trên phim ảnh từng quảng cáo thì không cách gì lý tưởng hơn là ngắm cảnh Vịnh Hạ L', '1', '');
INSERT INTO `activities` VALUES ('80', 'Ngủ đêm trên du thuyền', 'Tàu du lịch trên vịnh như một khách sạn thu nhỏ, cũng có phòng riêng, với chăn nệm sạch sẽ, điều hòa, có phòng tắm riêng. Thật thú vị khi bạn vừa có thể thư giãn ngay trong phòng riêng và mở cửa sổ ra bạn sẽ thấy nhìn những dãy núi, nước non hùng vĩ, nhữn', '1', '');
INSERT INTO `activities` VALUES ('81', 'Chèo thuyền Kayak', 'Nếu đi du lịch Hạ Long mà bạn không trải nghiệm qua các hành trình khám phá vịnh bằng thuyền Kayak thì quả là điều đáng hối tiếc. Khi ngồi trên chiếc thuyền Kayak bé nhỏ vừa khua mái chèo trên làn nước xanh, vừa ngắm cảnh thiên nhiên hút hồn, thật là điều', '1', '');
INSERT INTO `activities` VALUES ('82', 'Công viên Rồng (Dragon\n Park) & Công viên nước Typhoon (Typhoon Water Park)', 'nằm trong Tổ hợp Vui chơi giải trí Sun World Halong Park, là công viên giải trí đẳng cấp quốc tế với nhiều trò chơi mới lạ chưa từng có tại Việt Nam. Một thế giới những trò chơi phiêu lưu mạo hiểm được mở ra như Phi Long thần tốc, Tê Giác cuồng nộ, Theo d', '1', '');
INSERT INTO `activities` VALUES ('83', 'Cáp treo Nữ Hoàng', 'Hành trình nối từ ga Đại Dương đến ga Mặt Trời bằng cáp treo Nữ Hoàng là trải nghiệm không thể bỏ lỡ tại Sun World Hạ Long Park. Đây  là con đường duy nhất kết nối tổ hợp vui chơi giải trí ven biển với đỉnh Ba Đèo. Hơn nữa, du khách còn được chiêm ngưỡng ', '1', '');
INSERT INTO `activities` VALUES ('84', 'Vòng quay Mặt Trời', 'Ga đến của cáp treo là trung tâm vui chơi giải trí, điểm nhấn là vòng quay mặt trời khổng lồ trên đỉnh đồi Ba Đèo.\n\nSau khi lên tới đỉnh đồi Ba Đèo, hành khách có thể phóng tầm mắt nhìn trọn vẹn cầu Bãi Cháy, cảng Cái Lân, Hòn Gai và thành phố Hạ Long phí', '1', '');
INSERT INTO `activities` VALUES ('85', 'Tham quan Vinpearl\n Safari', 'Vinpearl Safari là mô hình vườn thú mở lớn nhất Việt Nam với diện tích khoảng 500 hecta, quy tụ khoảng 130 loài với hơn 2.200 cá thể động vật quý hiếm. Vinpearl Safari được chia gồm hai phân khu chính: khu vườn thú mở (open zoo) dành cho khách tham quan g', '1', '');
INSERT INTO `activities` VALUES ('86', 'Khu vui chơi giải trí \nVinpearl Phú Quốc', 'Được xây dựng trên tổng\n diện tích lên đến 170.000m², khu vui chơi giải trí Vinpearl Phú Quốc bao gồm nhiều địa điểm vui chơi hấp dẫn như: Công viên nước, Thủy Cung, Khu vui chơi ngoài trời, Rạp chiếu phim, Phố ẩm thực và mua sắm, Lâu đài cổ tích… Ngoài r', '1', '');
INSERT INTO `activities` VALUES ('87', 'Khám phá chợ đêm\n Dinh Cậu', 'Chợ đêm Dinh Cậu là khu chợ sầm uất trên đường Võ Thị Sáu, bắt đầu từ đường Bạch Đằng đến ngã ba Trần Hưng Đạo, kéo dài trên 200m. Chợ họp gần bãi biển, trung tâm thị trấn Dương Đông, cách Dinh Cậu chừng 100m. Chợ nhóm họp từ 17 giờ đến 21-22 giờ. Toàn kh', '1', '');
INSERT INTO `activities` VALUES ('88', 'Lặn biển ngắm san hô', 'Hệ thống sinh thái biển đa \ndạng thuộc quần đảo An Thới phía Nam hay hòn Móng Tay, hòn Đồi Mồi và hòn Thầy Bói phía Bắc đảo Phú Quốc là những nơi lý tưởng để du khách khám phá thế giới đại dương, đặc biệt hơn cả là các dải san hô nơi đây đứng đầu bảng tại', '1', '');
INSERT INTO `activities` VALUES ('89', 'Câu cá khám phá đảo\n hoang', 'Tour này sẽ đưa bạn đến với một hòn đảo hay bờ biển nhiều loài cá như cá mú, cá tràm, cá lù, cá đổng. Chiến lợi phẩm là cá câu đươc sẽ được phục vụ ngay trên tàu. Vừa câu cá, nhóm bạn vừa có thể tổ chức cắm trại, dã ngoại và khám phá thêm những vùng đất x', '1', '');
INSERT INTO `activities` VALUES ('90', 'Khám phá rừng nguyê\nn sinh', 'Nếu bạn là người ưa thích mạo hiểm thì đây chính là chọn lựa dành cho bạn. Bạn có thể đặt tour khám phá Bắc đảo hoặc đi Khu bảo tồn sinh thái suối Đá Ngọn hoặc Khu bảo tồn sinh thái Gành Dầu. Một số du khách ưa mạo hiểm tự mình khám phá rừmg nguyên sinh h', '1', '');
INSERT INTO `activities` VALUES ('91', 'Câu mực đêm', 'Khi màn đêm buông xuống là lúc để trải nghiệm những giây phút vừa thư giãn thú vị cùng các ngư dân với hoạt động câu mực đêm. Sẽ không gì bằng khi chính tay bạn câu được những con mực đang săn mồi dưới biển hay vớt được những chú cá kiếm, cá xanh xương đa', '1', '');
INSERT INTO `activities` VALUES ('92', 'Tắm biển, bật nhạc nằm chill trên bãi cát ', 'Đến Nam Du, tất nhiên việc\n đầu tiên bạn nghĩ đến sẽ là tắm biển, bởi trời ơi, làm sao bạn có thể cưỡng lại trước cảnh bãi cát trắng, nước trong vắt xanh như ngọc mà Nam Du bày ra trước mặt được cơ chứ?\n\nỞ đây, bạn có thể ghé nhiều bãi khác nhau để tắm bi', '1', '');
INSERT INTO `activities` VALUES ('93', 'Ngắm hoàng hôn', 'Chẳng ai ghé thăm biển đảo mà lại bỏ về khi chưa ngắm hoàng hôn, thật đấy! Có thể mỗi người lại có một lý do, một dự định riêng khi ngắm hoàng hôn ở biển, nhưng đến Nam Du rồi, dù bạn chẳng có lý do hay dự định gì thì bạn cũng chẳng thể rời mắt được cảnh ', '1', '');
INSERT INTO `activities` VALUES ('94', ' Lặn ngắm san hô', 'Từ hòn Mấu ghé qua hòn Dầu\n rất gần, vậy nên nếu bạn đang đi thăm hòn Mấu, hãy nhắn thuyền đưa ra hòn Dầu chơi. Hòn Dầu nức tiếng với những rặng san hô dày và tuyệt đẹp, vậy nên bạn nhất quyết không được quên kính bơi và đồ bơi để lặn ngắm san hô đâu đấy.', '1', '');
INSERT INTO `activities` VALUES ('95', ' Ghé thăm làng chài \n', 'Ở Nam Du, du lịch mới chỉ bắt đầu\n nhen nhóm, vậy nên bạn sẽ chẳng tìm được một dịch vụ sang chảnh, hoành tráng được trang bị tận răng đâu. Nhưng vậy lại hay, bởi bạn sẽ được những người dân bản địa đưa đi thăm thú, nhìn ngắm những vẻ đẹp, những lát cắt c', '1', '');
INSERT INTO `activities` VALUES ('96', 'Cắm trại bên bờ biển ', 'Nam Du rất hợp để đi theo nhóm, \nvậy nên nếu bạn ưa phiêu lưu, yêu cuộc sống hoang sơ thì đừng bỏ qua cơ hội cắm trại ở nơi này. Nam Du có rất nhiều điểm cắm trại được, và rất đẹp. Cứ nghĩ đến cảnh đêm nằm nghe sóng vỗ rì rào, rồi sáng mở mắt thấy bình mi', '1', '');
INSERT INTO `activities` VALUES ('97', ' Nhất định phải ăn \ncá xương xanh, cháo mực', 'Đừng đến Nam Du nếu bạn không\n biết ăn hải sản, hoặc bị dị ứng. Đùa thôi, nhưng sự thật là, hải sản ở Nam Du ngon đến nỗi, bạn chỉ ước giá như ngày nào mình cũng được ních thoả thích một bụng đầy. Hải sản tươi vừa mới đánh bắt, những con nhum còn thơm mùi', '1', '');
INSERT INTO `activities` VALUES ('98', 'Lên hải đăng ngắm cảnh', 'Là một hòn đảo đẹp và còn nhiều\n nét hoang sơ, vậy nên sẽ rất tuyệt nếu bạn ngắm được toàn cảnh từ trên hải đăng. Hải đăng ở đây tui không quá hoành tráng, thế nhưng lại là nơi ngắm đảo đẹp nhất, có thể giúp bạn dễ dàng bao quát khung cảnh toàn bộ hòn đảo', '1', '');
INSERT INTO `activities` VALUES ('99', 'thuê xe máy đi thăm \nThạch Động, Núi Đá Dựng, Khu du lịch Mũi Nai.', 'Tại đây bạn có thể thuê chỗ ngồi \nbên bờ biển tắm biển, ngắm sóng vỗ, đón gió biển, thưởng thức các món hải sản.\nTiếp tục hành trình Mũi Đèn, rồi đến Chùa Hang, bạn sẽ mất khá nhiều thời gian ở đây bởi phong cảnh đẹp sẽ níu giữ chân bạn. Sau khi thăm quan', '1', '');
INSERT INTO `activities` VALUES ('100', 'Đi dạo và mua sắm chợ đêm Hà Tiên', 'Buổi tối có thể đi dạo thị trấn, vào\n Chợ đêm chơi (Chợ đêm Hà Tiên nằm gần khu vực cầu phao cũ, nay là bến tàu du lịch. Chợ bày bán các đồ lưu niệm, các loại đặc sản phụ vụ nhu cầu của du khách.)', '1', '');
INSERT INTO `activities` VALUES ('101', 'Tắm biển, câu mực,\ncá ban đêm và lặn biển bắt sò, ghẹ, ốc…', 'Trên đảo có bãi biển cát trắng rất\n đẹp trải dài mấy trăm mét, bạn có thể tắm biển thoải mái, chơi các trò chơi trên bãi các tự túc, hoặc thuê kính lặn biển đi ngắm san hô gần bờ, mò cua bắt ốc quanh các gành đá có rất nhiều.\nMột trong những hoạt động khô', '1', '');
INSERT INTO `activities` VALUES ('102', 'Bắt hào', 'lặn xuống biển cạy hào \nbám ở ghềnh đá để thưởng thức.', '1', '');
INSERT INTO `activities` VALUES ('103', ' Tham quan khu đô thị lấn \nbiển', 'đi dạo hóng gió biển, uống cafe, \ndạo đường Tôn Đức Thắng ', '1', '');
INSERT INTO `activities` VALUES ('104', 'Tham quan đình Nguyễn\n Trung Trực', 'Đến đây du khách không chỉ tham quan, mà còn cầu an cho mọi điều tốt lành sẽ đến, cầu cho mưa thuận gió hòa, vụ mùa bội thu,.. ', '1', '');
INSERT INTO `activities` VALUES ('105', 'Đi thuyền thăm quan các đảo', 'Do quần đảo Bà Lụa có rất nhiều \nđảo nhỏ khác, dịch vụ du lịch không phải đảo nào cũng có, nếu muốn tham quan hết cũng hết sức khó khăn vì vậy bạn nên chọn một vài quần đảo đẹp để đi. Ba Hòn Đầm (Hòn Đước, hòn Đàm Dương, hòn Giếng) là những hòn đảo đẹp đư', '1', '');
INSERT INTO `activities` VALUES ('106', 'Cắm trại trên biển', 'Sẽ thật đáng tiếc nếu bạn không\n cắm trại ở biển 1 đêm, trải nghiệm cảm giác nằm nghe tiếng sóng vỗ ngay bên tai mình hay cảm giác bị đánh thức bởi những tia nắng mặt trời sớm mai len qua các lều trại. Buổi tối các nhóm có thể quây quần bên đống lửa trại,', '1', '');
INSERT INTO `activities` VALUES ('107', 'Thử làm ngư dân trên đảo', 'Hải sản trên đảo khá phong phú, chẳng cần tàu thuyền, chẳng cần ra ngoài khơi xa bạn cũng có thể kiếm cho mình một ít hải sản ngay tại các bãi biển, trên các ghềnh đá. Cua, ốc, hàu… là loại hải sản dễ tìm thấy và dễ bắt nhất, hãy thử cảm giác của một ngư ', '1', '');
INSERT INTO `activities` VALUES ('108', 'Ngắm hoàng hôn và bình \nminh trên đảo', 'Hoàng hôn và bình minh trên đảo Bà Lụa có lẽ là thời khắc đẹp nhất của thiên nhiên. Bạn sẽ không khỏi ngạc nhiên thích thú khi mặt trời mọc và lặn xuống biển với ráng hồng tuyệt đẹp, xa xa một vài chiếc thuyền nghe đang lên trên biển. Không khí trở nên tĩ', '1', '');
INSERT INTO `activities` VALUES ('109', 'Câu cá', 'Hồ Hoa mai một địa điểm ưa thích \ncủa các tín đồ  câu cá. Vì ở đây khu câu cá phục vụ nhu cầu giải trí của du khách. Chỉ mất khoảng 40.000đồng/vé/người/ngày cộng với 30.000 đồng thuê một chiếc xuồng 3 lá  là bạn có thể thỏa thích câu cá, với số lượng cá k', '1', '');
INSERT INTO `activities` VALUES ('110', 'Đu dây vượt thác Datanla', 'Những ai yêu thích mạo hiểm và muốn thử thách tinh thần thép của bản thân thì chắc chắn sẽ rất thích thú với trải nghiệm này. Với địa điểm là thác Datanla, một trong những dòng thác hùng vĩ, vách đá cheo leo và đẹp nhất của Đà Lạt, bạn sẽ cảm nhận rõ được', '1', '');
INSERT INTO `activities` VALUES ('111', 'Tham gia máng trượt thác', 'Với chiều dài là 1.650m chạy uốn \nlượn qua những dòng suối ở giữa khu rừng nguyên sinh để đến thác Đasara bốn tầng nằm dưới chân thác Dambri hùng vĩ, hệ thống máng trượt này được đầu tư hoành tráng và xây dựng nên nhằm thỏa mãn nhu cầu thám hiểm vẻ đẹp củ', '1', '');
INSERT INTO `activities` VALUES ('112', 'Chinh phục đỉnh Langbiang \nhuyền thoại', 'LangBiang được xem như nóc nhà\n Đà Lạt và là nơi tôn thờ tình yêu vĩnh cửu giữa chàng K’lang và nàng H’biang xinh đẹp. Núi LangBiang nằm trong khu vực bảo tồn LangBiang có diện tích 275.439 ha, được UNESCO công nhận là Khu dự trữ sinh quyển thế giới. Có 2', '1', '');
INSERT INTO `activities` VALUES ('113', 'Uống cà phê, sữa đậu nành\n nóng ở Đà Lạt', 'Hãy thử tưởng tượng xem, trong \ncái trong khí se lạnh của buổi sớm tinh mơ hay trời chập tối mà được thưởng thức những ly cà phê hay sữa đậu nóng ấm thì cảm giác tuyệt vời như thế nào. Ngoài ra, Đà Lạt còn là xứ sở của cà phê nên không khó để bạn tìm thấy', '1', '');
INSERT INTO `activities` VALUES ('114', 'Khám phá ẩm thực Đà Lạt tại\n chợ đêm', 'Vốn không chỉ nổi tiếng với các\n món nướng và lẩu mà tại chợ Đà Lạt còn tập trung vô số những món ăn ngon và hấp dẫn khác từ khắp các vùng miền. Điểm chung ở đây chính là các món ăn đều theo phương châm “nhanh – rẻ – ngon” nên sẽ thỏa mãn được bất kỳ nhu ', '1', '');
INSERT INTO `activities` VALUES ('115', 'Vượt cầu thang bộ hàng trăm\n mét để tham quan', 'Để tham quan hết các tầng thác ở \nDatanla bằng việc đi bộ qua những cung bậc thang được xây bằng xi măng, phủ đầy rêu xanh trơn trượt, quả thật là một thách thức không nhỏ. Nó đòi hỏi bạn phải có sức khỏe, yêu thích khám phá mới lặn lội, vượt hàng trăm mé', '1', '');
INSERT INTO `activities` VALUES ('116', 'Đi cáp treo ngắm thác và rừng xanh trên cao', 'Cáp treo đi xuyên qua rừng, thác,\n núi cao, và những vách đá... mang lại cho du khách nhiều ấn tượng bất ngờ khi được chiêm ngưỡng vẻ đẹp non nước của vùng đất này từ trên cao.', '1', '');
INSERT INTO `activities` VALUES ('117', 'Trượt thác', 'Trải nghiệm trượt thác hoặc chèo\n thuyền qua các ghềnh thác thu hút không chỉ khách địa phương, trong nước mà còn cả các bạn quốc tế. Tuy có phần nguy hiểm, hoạt động này mang lại cho bạn cảm giác sảng khoái, khỏe khoắn và nhất là được thưởng thức những t', '1', '');
