INSERT INTO `festivals` VALUES ('1', 'Ngày hội cây trái ngon', 'Là lễ hội truyền thống nhằm giới thiệu các loại trái cây, cây giống ở địa phương chợ Lách tỉnh Bến Tre qua đó xây dựng mối liên kết giữa nhà nông, doanh nghiệp và nhà khoa học để phát triển nông nghiệp bền vững.', 'Tháng 5', '1', '1');
INSERT INTO `festivals` VALUES ('2', 'Lễ hội truyền thống văn hóa tỉnh', 'Là lẽ hội kỷ nhằm kỷ niệm ngày sinh của nhà thơ yêu nước Nguyễn Đình Chiểu, đồng thời kỷ niểm ngày truyền thống văn hóa tỉnh.', 'Tháng 5', '1', '1');
INSERT INTO `festivals` VALUES ('3', 'Lễ hội tế thần cá ông', 'Lễ hội nghinh Ông là lẽ hội văn hóa truyền thống mang tính cộng đồng của cư dân miền biển vừa mang ý nghĩa tâm lình vừa cầu mong một mùa đánh bắt thuận lợi, bình an nơi biển cả.', 'Tháng 6 âm lịch', '1', '1');
INSERT INTO `festivals` VALUES ('4', 'Lễ hội dừa', 'Là lễ hội đặc trưng của vùng đất xứ dừa, lễ hội được tổ chức hai năm một lần và thường gắn liền với ngày hội truyền thống Cách mạng.', 'Tháng 4', '1', '1');
INSERT INTO `festivals` VALUES ('5', 'Hội đình Phú Lễ', 'Phần lễ trong hội đình Phú Lễ gồm có rước sắc thần, tế lễ Thành hoàng, Tiền, hậu hền (người khai khẩn, khai cơ). Phần hội, đêm hội có hát bội và ca nhạc tài tử. Lễ hội cầu cho mưa thuận gió hòa, mùa màng tươi tốt.', 'Tháng 3 âm lịch', '1', '1');
INSERT INTO `festivals` VALUES ('6', 'Lễ hội Nguyễn Đình Chiểu', 'Đến với lễ hôi, du khách có cơ hội tham quan triển lãm ảnh về thành tựu kinh tế - xã hội của tỉnh Bến Tre và các tác phẩm thơ văn của Nguyễn Đình Chiểu cũng như tìm hiểu về thân thế và sự nghiệp văn chương của nhà thơ.', 'Tháng 7', '1', '1');
INSERT INTO `festivals` VALUES ('7', 'Lễ hội Núi Bà Đen', 'Lễ được tổ chức vào các ngày mùng 4,5,6 tháng 5 âm lịch. Vào lúc không giờ đêm mùng 3 rạng mùng 4, lễ tắm bà được tổ chức trang nghiêm tại đền thờ', 'Ngày 4, 5, và 6 tháng 5 âm lịch', '2', '1');
INSERT INTO `festivals` VALUES ('8', 'Hội xuân núi Bà', 'Sau tết nguyên đán, tiết xuân mát mẻ, du khách từ bốn phương kéo về núi Bà ở Tây Ninh mở hội xuân. Bắt đầu từ ngày mùng 4 tháng Giêng, núi Bà trở nên đông vui, tấp nập và kéo dài trong suốt tháng giêng.', 'Tháng giêng', '2', '1');
INSERT INTO `festivals` VALUES ('9', 'Lễ hội truyền thống Động Kim Quang', ' Lễ hội Động Kim Quang là dịp để tuổi trẻ Hòa Thành được ôn lại truyền thống Cách mạng, tưởng nhớ công lao của các liệt sĩ đã hy sinh vì sự nghiệp giải phóng dân tộc.', 'Ngày 14 tháng Giêng âm lịch', '2', '1');
INSERT INTO `festivals` VALUES ('10', 'Lễ hội của Đạo Cao Đài Tây Ninh', 'Lễ hội của Đạo Cao Đài không chỉ đơn thuần là dấu ấn của một tôn giáo, mà đó còn là dịp để các tín đồ trong đạo tập hợp lại, cùng nhau sinh hoạt, hướng đến điều thiện và tích góp công đức cho tương lai.', 'Tháng 8 âm lịch', '2', '1');
INSERT INTO `festivals` VALUES ('11', 'Lễ hội “Nào Cống”', 'Lễ hội nào cống là lễ hội của các đồng bào dân tộc thiểu số H\'Mông, Dao, Giáy. Xuất hiện từ thập kỷ 50 về trước, Tả Van có một ngôi miếu thờ 3 gian. Ngôi miếu dựng ở ngay đầu cầu treo sang làng Tả Van Giáy. Ngôi miếu trở thành địa điểm tổ chức lễ \"Nào Cốn', 'Tháng 6 âm lịch', '3', '1');
INSERT INTO `festivals` VALUES ('12', 'Lễ Tết nhảy', 'Lễ tết nhảy là lễ hội của đồng bào dân tộc Dao Quần Chẹt, nhằm hậu tạ tổ tiên và chuẩn bị làm lễ hứa đầu năm mới. ', 'Ngày 15/12 âm lịch', '3', '1');
INSERT INTO `festivals` VALUES ('13', 'Lễ hội “Nhặn Sồng” & “Nào Sồng”', 'Lễ hội “Nhặn Sồng” và “Nào Sồng” của người Dao đỏ là một lễ hội ý nghĩa với ước mong bảo vệ rừng, chống thả rông gia súc, quy ước còn đề cập đến các vấn đề phòng chống trộm cắp, bảo vệ mùa màng, tương trợ giúp đỡ lẫn nhau…', '', '3', '1');
INSERT INTO `festivals` VALUES ('14', 'Lễ hội Roóng Poọc của người Giáy', 'Tuy vốn là lễ hội dân tộc truyền thống của người Giáy ở Tả Van, nhưng nhiều năm nay đã lan rộng, trở thành lễ hội chung của cả vùng thung lũng Mường Hoa.', 'Tháng giêng', '3', '1');
INSERT INTO `festivals` VALUES ('15', 'Hội Gầu Tào của người Mông ở Sapa', 'Là hoạt động nhằm tôn vinh bảo tồn, phát huy bản sắc văn hóa dân tộc và giới thiệu với du khách những nét văn hoá truyền thống của dân tộc Mông San Sả Hồ.', 'Tháng giêng', '3', '1');
INSERT INTO `festivals` VALUES ('16', 'Lễ hội Xuống đồng Sapa', 'Lễ hội xuống đồng là lễ hội để kết thúc một tháng Tết vui chơi, lễ hội quy tụ những sắc thái văn hóa đặc trưng nhất của mỗi dân tộc ở mỗi vùng miền khác', 'Mùng 8 Tết', '3', '1');
INSERT INTO `festivals` VALUES ('17', 'Lễ hội Hoa chuối của người Xá Phó', 'Hội hoa chuối là nơi tụ họp vui chơi, cầu chúc, múa hát, thể hiện tinh thần đoàn kết, nhớ ơn tổ tiên, cùng phấn đấu vươn lên trong cuộc sống, lao động của người dân tộc Xa Phó.', 'Ngày 9 tháng 9', '3', '1');
INSERT INTO `festivals` VALUES ('18', 'Tết đón hồn lúa mới (Tết cơm mới)', 'Tết cơm mới là một nghi lễ rất quan trọng để cảm ơn tổ tiên, trời đất đã phù hộ cho gia đình, cộng đồng có vụ mùa bội thu. Khi các cánh đồng lúa đã ngả màu, chuẩn bị cho một mùa thu hoạch mới, các gia đình trong làng chọn lấy một ngày tốt, ngày đẹp để tổ ', '', '3', '1');
INSERT INTO `festivals` VALUES ('19', 'Dinh Thầy Thím', 'Là một trong số ít những lễ hội phía Nam được đưa vào từ điển Lễ hội Việt Nam, Lễ hội Dinh Thầy Thím với khung cảnh thiên nhiên hữu tình và nét đặc sắc của riêng mình, đã mỗi ngày thêm thu hút đông đảo du khách gần xa tới Phan Thiết.', 'Từ ngày 14 đến ngày 16 tháng 9 âm lịch', '4', '1');
INSERT INTO `festivals` VALUES ('20', 'Lễ hội Nghinh Ông', 'Lễ hội Nghinh Ông là lễ hội của đồng bào người Hoa tại phan thiet tưởng nhớ đến Quan Thanh Đế quân (Quan Công), mang ý nghĩa văn hóa dân gian, thể hiện mong ước sự bình an, hạnh phúc và cuộc sống ấm no cho cả cộng đồng.', 'Ngày 24 và 25 tháng 7 âm lịch', '4', '1');
INSERT INTO `festivals` VALUES ('21', 'Lễ hội Cầu ngư', 'Cầu Ngư là lễ hội dân gian gắn liền với tín ngưỡng và đời sống cư dân ven biển miền Trung. Ở Bình Thuận, lễ hội Cầu Ngư có ở hầu hết các dinh vạn thờ cá Ông (cá Voi) trong tỉnh.', 'Ngày 20 tháng 6 âm lịch', '4', '1');
INSERT INTO `festivals` VALUES ('22', 'Lễ hội Trung thu', 'Lễ hội Trung thu tại thành phố Phan Thiết đã được sắc kỷ lịch Guiness Việt Nam công nhận là lễ hội Trung thu lớn nhất trong nước thu hút đông đảo du khách và nhân dân địa phương cùng tham gia.', 'Ngày 14 tháng 8 âm lịch', '4', '1');
INSERT INTO `festivals` VALUES ('23', 'Lễ hội đua thuyền', 'Đây là một trong những hoạt động thể thao lâu đời, gắn bó với đời sống văn hóa tinh thần của người dân nơi đây mỗi dịp xuân về.', 'Mùng 2 Tết', '4', '1');
INSERT INTO `festivals` VALUES ('24', 'Lễ hội Katê', 'Đây là lễ tết để tưởng nhớ các anh hùng, thần linh, tổ tiên, các vị vua có công với nước, với dân đã được thần thánh hóa như: Pôklông Garai, Pôrômê. Lễ hội đồng thời với việc hành hương, là dịp viếng thăm, gặp gỡ các người thân...', 'Từ ngày 25 tháng 9 đến ngày 5 tháng 10', '4', '1');
INSERT INTO `festivals` VALUES ('25', 'Lễ hội Cầu yên', 'Là một trong những lễ hội truyền thống của người Chăm tại Phan Thiết , được tổ chức hằng năm tại các xóm làng, xóm vùng dân tộc Chăm Bà Ni.', 'Đầu tháng Giêng', '4', '1');
INSERT INTO `festivals` VALUES ('26', 'Lễ điện Hòn Chén', 'Nghi lễ tại Ðiện Hòn Chén rất long trọng. Dân làng tổ chức tế tại đình, trước ngày chánh tế có lễ nghinh thần để rước tất cả các vị thần trong làng về đình. Ðám rước Thiên Y A Na Thánh Mẫu từ Huệ Nam đến đình làng Hải Cát tổ chức trọng thể hơn cả.', 'Ngày 2 ngày 3 tháng 3 và tháng 7', '5', '1');
INSERT INTO `festivals` VALUES ('27', 'Hội đua ghe truyền thống', 'Hội đua ghe truyền thống tỉnh Thừa Thiên Huế là một ngày lễ hội mới được tổ chức sau ngày giải phóng hoàn toàn miền Nam 1975. Hội được tổ chức trong một ngày, nhằm ngày lễ Quốc Khánh 02-9 (dương lịch). Địa điểm đua là bờ Sông Hương trước trường Quốc Học.', 'Ngày 2/9', '5', '1');
INSERT INTO `festivals` VALUES ('28', 'Lễ hội cầu ngư Thái Dương Hạ', 'Hội được tổ chức vào ngày 12 tháng Giêng để tưởng nhớ vị thành hoàng của làng là Trương Quý Công biệt danh của Trương Thiều, người gốc miền Bắc, có công bày cho dân nghề đánh cá và buôn bán ghe mành.', 'Ngày 12 tháng Giêng', '5', '1');
INSERT INTO `festivals` VALUES ('29', 'Hội vật Làng Sình', 'Lễ hội đấu Vật làng Sình được tổ chức tại khu vực đình làng Lại Ân (còn gọi là làng Sình), xã Phú Mậu, huyện Phú Vang (TT-Huế) để cầu sức khỏe, bình an, mùa màng bội thu, cũng là dịp để giải trí ngày đầu Xuân và khuyến khích thanh niên rèn luyện sức khỏe,', 'Ngày 10 tháng Giêng', '5', '1');
INSERT INTO `festivals` VALUES ('30', 'Hội chợ xuân Gia Lạc', 'Khác với những phiên chợ bình thường khác, chợ Xuân gia lạc lại họp vào ngày mùng 1 tết đầu năm.Những người đi chợ không quan trọng việc mua bán lỗ lãi mà chỉ coi đây là một dịp đi du xuân, cầu may.', 'Mùng 1 Tết', '5', '1');
INSERT INTO `festivals` VALUES ('31', 'Lễ hội Cố Đô Hoa Lư', 'Lễ hội Hoa Lư (tên cũ lễ hội Trường Yên) là một lễ hội truyền thống diễn ra hàng năm để tôn vinh vị anh hùng dân tộc Đinh Bộ Lĩnh đã xây dựng kinh đô Hoa Lư, lập ra nhà nước Đại Cồ Việt và mở đầu thời kỳ độc lập, thống nhất lâu dài của người Việt suốt các', '6/3 đến 10/3 âm lịch', '7', '1');
INSERT INTO `festivals` VALUES ('32', 'Lễ hội đền Thái Vi', 'Đây là dịp để nhân dân Ninh Bình và nhân dân cả nước tưởng nhớ công lao các vua Trần - những người có công lớn với dân với nước. \nPhần lễ được tiến hành dưới hai hình thức: rước kiệu và tế. Rước kiệu ở đền Thái Vi không chỉ có một đoàn, mà là trên 30 đoàn', 'từ ngày 14 đến ngày 17 tháng 3 âm lịch', '7', '1');
INSERT INTO `festivals` VALUES ('33', 'Lễ hội đền Địch Lộng ', 'Phần lễ tổ chức dâng hương và lễ Phật theo nghi thức nhà Phật. \nPhần hội cũng tổ chức các trò chơi dân gian như: múa lân, múa rồng, cờ tướng, thi viết chữ nho... ', 'mùng 6 và mùng 7 tháng 3 âm lịch', '7', '1');
INSERT INTO `festivals` VALUES ('34', 'Lễ hội chùa Bái Đính', 'Cũng như các lễ hội khác, hội gồm có hai phần là phần lễ và phần hội. \nPhần lễ thường tổ chức dâng hương, tưởng nhớ các vị anh hùng có công với nước với dân. \nPhần hội kéo dài từ 3 đến 5 ngày, tổ chức các trò chơi dân gian như: đánh cờ, đấu vật... ', 'mồng 6 tháng giêng', '7', '1');
INSERT INTO `festivals` VALUES ('35', 'Lễ hội Báo bản Nộn Khê', 'Phần lễ ngoài việc tổ chức tế, dâng hương tôn vinh công đức của các vị tiền bối lập ra làng xã, còn dâng hương tưởng nhớ, tỏ lòng biết ơn các liệt sỹ là con em của làng. Một nét độc đáo của lễ hội Báo bản là kính báo lên Thành Hoàng, các bậc tiên tổ về sự', 'ngày 14 tháng giêng âm lịch', '7', '1');
INSERT INTO `festivals` VALUES ('36', 'Lễ hội đền Nguyễn Công Trứ ', 'Ngày 13 tế yết cáo, ngày 14 tế chính kỵ, ngày 15 tế tạ. Hình thức tế theo phong tục tế lễ như các lễ hội khác. \nPhần hội có các trò dân gian như: múa lân, đấu vật, thi bơi trải trên sông Ân... ', 'từ ngày 13 đến ngày 15 tháng 11 âm lịch', '7', '1');
INSERT INTO `festivals` VALUES ('37', 'Lễ hội đình Bình Thủy', ' ngoài việc cúng tế vào ngày rằm mỗi tháng và tết Nguyên Đán. Một năm đình còn có 2 kỳ lễ hội lớn gắn liền dấu ấn sản xuất nông nhiệp. Lễ thượng điền để cúng đất đai bắt đầu vụ mùa mới vào ngày 14/4 và 15/4 âm lịch và lễ hạ điền để tạ ơn và cúng ruộng đồn', '14/4 - 15/4 và 15/12  âm lịch', '11', '1');
INSERT INTO `festivals` VALUES ('38', 'Lễ đấu đèn', 'Lễ đấu đèn được tổ chức 10 năm một lần. Lễ đấu đèn diễn ra ngay trong chánh điện của chùa Ông, mọi người đều có thể tham gia để được sở hữu chiếc đèn mình yêu thích. Theo quan niệm của người Hoa, được sở hữu chiếc đèn lồng từ lễ hội 10 năm mới có một lần ', '10 năm', '11', '1');
INSERT INTO `festivals` VALUES ('39', 'Lễ hội Nghinh Ông', 'Thời gian: từ ngày 16 đến 18/8 âm lịch.\nĐịa điểm: Đình thần Thắng Tam, đường Hoàng Hoa Thám, thành phố Vũng Tàu.\nKhông chỉ được Tổng Cục Du lịch chọn là một trong 15 lễ hội lớn của cả nước, lễ hội Nghinh Ông còn được xem là nét đẹp văn hoá tiêu biểu của n', ' 16 đến 18/8  âm lịch', '12', '1');
INSERT INTO `festivals` VALUES ('40', 'Lễ hội Trùng Cửu', 'Thời gian: Đêm mùng 8 và ngày mùng 9/9 âm lịch.\nĐịa điểm: Nhà Lớn Long Sơn, thôn 10, xã Long Sơn.\nĐây được xem là lễ hội cầu an cho toàn dân được mạnh khỏe, an lành, hạnh phúc. Đêm mùng 8/9 gọi là lễ Tiên Thường kỉnh mặn (cúng mặn), ngày mùng 9/9 gọi là l', '8/9 - 9/9 âm lịch', '12', '1');
INSERT INTO `festivals` VALUES ('41', 'Lễ giỗ Đức Thánh\nTrần', 'Thời gian: ngày 20/8 âm lịch.\nĐịa điểm: Hội đền Đức Thánh Trần, số 68 - Hạ Long, phường 2.\nĐây là nghi lễ được người dân Vũng Tàu tổ chức hàng năm theo tập tục dân gian, nhằm bày tỏ lòng biết ơn, kính trọng đối với Thánh tướng Trần Hưng Đạo, người ba lần ', ' 20/8 âm lịch', '12', '1');
INSERT INTO `festivals` VALUES ('42', 'Lễ hội Miếu Bà Ngũ Hành', 'Thời gian: từ ngày 16 đến 18/10 âm lịch.\nĐịa điểm: Miếu Bà Ngũ Hành, đường Hoàng Hoa Thám, phường Thắng Tam.\nMiếu Bà Ngũ Hành do ngư dân Vũng Tàu lập nên để tôn thờ 5 vị Thần nữ là: Kim, Mộc, Thủy, Hỏa, Thổ cùng Thủy Long Thần Nữ, Thánh Mẫu Thiên Y Ana, n', '16 đến 18/10 âm lịch.', '12', '1');
INSERT INTO `festivals` VALUES ('43', 'Lễ hội bắn súng Thần\n Công', 'Thời gian: Tổ chức vào những dịp khai hội đầu năm và các sự kiện lịch trọng đại của thành phố và tỉnh Bà Rịa – Vũng Tàu.\nĐịa điểm: Di tích Lịch sử - Văn hóa Bạch Dinh, số 10 - Trần Phú.\nLễ hội là nét riêng đặc biệt của thành phố biển Vũng Tàu. Điểm nhấn c', 'Những ngày đầu năm', '12', '1');
INSERT INTO `festivals` VALUES ('44', 'Lễ hội Dinh Cô', 'Dinh Cô là một công trình kiến trúc uy nghi, được xây dựng trên một diện tích rộng lớn vào cuối thế kỷ XVIII, nằm bên sườn đồi nhỏ, trước mặt là bãi biển Long Hải.\nTheo truyền thuyết kể rằng: cách đây 200 năm có một cô gái trẻ tên Lê Thị Hồng (tục danh là', '10, 11, 12 tháng 2 âm lịch ', '12', '1');
INSERT INTO `festivals` VALUES ('45', 'Lễ hội Nam Hải', 'Lễ hội “Nam Hải Đại tướng Quân”  Danh hiệu Cá Ông do vua Thiệu Trị ban tặng được tổ chức trùng với ngày Vía (ngày mất) của cá. Lễ hội kéo dài trong 3 ngày, từ 16 đến 18 tháng 8 âm lịch hàng năm.\nLế hội gồm có lễ cúng Ông, lễ nghinh Ông (đón cá). Nhiều ghe', '16 đến 18/8 âm lịch', '12', '1');
INSERT INTO `festivals` VALUES ('46', 'Lễ hội Đình Thần \nThắng Tam', 'Hàng năm lễ hội được tổ chức trong 4 ngày, từ ngày 17-20 tháng 2 âm lịch. Đây là lễ hội cầu an, nhân thời điểm kết thúc và mở đầu cho một mùa thu hoạch tôm cá. Phần lễ diễn ra rất cầu kỳ: cúng tế, lễ vật tế thần, dâng hương quỳ lạy, chiêng trống, kèn nhạc', '17-20/2 âm lịch', '12', '1');
INSERT INTO `festivals` VALUES ('47', 'Lễ hội Yên Tử', 'Địa điểm: Diễn ra ở vùng núi Yên Tử thuộc xã Thượng Yên Công, TP Uông Bí.\nThời gian: Hàng năm được tổ chức bắt đầu từ ngày 10 tháng giêng và kéo dài hết tháng 3 (âm lịch).\nÝ nghĩa: Yên Tử là trung tâm Phật giáo của nước Đại Việt thuở trước, nơi phát tích ', ' 10 tháng giêng và kéo dài hết tháng 3 (âm lịch)', '13', '1');
INSERT INTO `festivals` VALUES ('48', 'Lễ hội Bạch Đằng', 'Địa điểm: Diễn ra tại xã Yên Giang, TX Quảng Yên.\nThời gian: Được tổ chức vào ngày mùng 8 tháng 3 (âm lịch) hàng năm, có năm kéo dài tới 4 ngày đêm.\nÝ nghĩa: Lễ hội kỷ niệm chiến thắng Bạch Đằng của những người anh hùng dân tộc chống giặc ngoại xâm: Ngô Q', '8/3 (âm lịch)', '13', '1');
INSERT INTO `festivals` VALUES ('49', 'Lễ hội Thập Cửu \nTiên Công', 'Địa điểm: Diễn ra ở đền Thập Cửu Tiên Công, thuộc xã Cẩm La, đảo Hà Nam, TX Quảng Yên.\nThời gian: Hàng năm dân làng mở hội vào ngày 7 tháng giêng (âm lịch).\nÝ nghĩa: Đền Thập Cửu Tiên Công (còn gọi là miếu Tiên Công) thờ 19 vị Tiên Công - những người có c', '7 tháng giêng (âm lịch)', '13', '1');
INSERT INTO `festivals` VALUES ('50', 'Lễ hội chùa Long\n Tiên', 'Địa điểm: Diễn ra tại chùa Long Tiên dưới chân núi Bài Thơ, thành phố Hạ Long.\nThời gian: Chính hội vào ngày 24 tháng 3 âm lịch hàng năm. \nÝ nghĩa: Chùa Long Tiên được xây dựng cách đây không lâu (năm 1941) nhưng là ngôi chùa lớn nhất ở thành phố Hạ Long.', ' 24 tháng 3 âm lịch', '13', '1');
INSERT INTO `festivals` VALUES ('51', ' Lễ hội đền Cửa Ông', 'Địa điểm: Diễn ra tại đền Cửa Ông, phường Cửa Ông, TP Cẩm Phả.\nThời gian: Được tổ chức từ ngày mùng hai tháng giêng cho đến hết tháng 3 (âm lịch).\nÝ nghĩa: Đền Cửa Ông thờ Trần Quốc Tảng, con thứ ba của Trần Hưng Đạo cùng nhiều tướng lĩnh nhà Trần có công', 'mùng hai tháng giêng cho đến hết tháng 3 (âm lịch)', '13', '1');
INSERT INTO `festivals` VALUES ('52', 'Lễ hội Quan Lạn', 'Địa điểm: Diễn ra ở bến Đình thuộc xã Quan Lạn, huyện Vân Đồn. \nThời gian: Được tổ chức vào ngày 18 tháng 6 (âm lịch) hàng năm nhưng lễ hội kéo dài từ ngày 10 đến hết ngày 20 tháng 6.  \nÝ nghĩa: Lễ hội Quan Lạn vừa kỷ niệm chiến thắng giặc Nguyên Mông năm', '10 - 18/6 âm lịch', '13', '1');
INSERT INTO `festivals` VALUES ('53', 'Lễ hội Trà Cổ', 'Địa điểm: Diễn ra tại làng Trà Cổ, TP Móng Cái.\nThời gian: Được tổ chức bắt đầu từ ngày 30 tháng 5 đến ngày mùng 6 tháng 6 (âm lịch) hàng năm.\nÝ nghĩa: Cách đây gần 600 năm, người Trà Cổ đã xây dựng được ngôi đình làng để thờ các vị tổ (Thành Hoàng làng).', ' 30/5 đến ngày mùng 6/6 (âm lịch) ', '13', '1');
INSERT INTO `festivals` VALUES ('54', 'Lễ hội Nguyễn\n Trung Trực', 'Nguyễn Trung Trực, vị anh hùng dân tộc mà tên tuổi gắn liền với những chiến công vang dội đã đi vào trang sử hào hùng, đó là: trận đốt cháy tàu Espérance trên vàm Nhật Tảo năm 1861 và lần đầu tiên tiêu diệt đồn lũy đầu não của giặc Pháp ngay tại tỉnh lỵ R', ' 26, 27 và 28 tháng Tám (âm lịch)', '19', '1');
INSERT INTO `festivals` VALUES ('55', 'Lễ hội Tao Đàn\n Chiêu Anh Các – Hà Tiên', 'Đã nhiều năm qua, mỗi dịp Tết Nguyên tiêu, rằm tháng Giêng hàng năm, tại Lăng Mạc Cửu, Hà Tiên lại tổ chức Lễ hội kỷ niệm Tao đàn Chiêu Anh Các. Vào mùa xuân năm Bính Thìn 1736, tại trấn Hà Tiên xưa, Tổng binh đại đô đốc Mạc Thiên Tích, hiệu Sĩ Lân đã cùn', ' rằm tháng Giêng âm lịch', '19', '1');
INSERT INTO `festivals` VALUES ('56', 'Lễ hội Oóc–om–\nbok – Gò Quao', 'Lễ hội Oóc–om–bok hay còn gọi là lễ cúng Trăng được tổ chức thống nhất vào đêm 15/10 âm lịch. Các vật cúng trong lễ hội là cốm dẹp, khoai, đậu, dừa… Đồng bào phật tử tập hợp lại xung quanh sân chánh điện, chờ đến khi mặt trăng lên đến đỉnh là mọi người đề', 'đêm 15/10 âm lịch', '19', '1');
INSERT INTO `festivals` VALUES ('57', 'Lễ hội giỗ 4 nhà\n sư liệt sĩ – Châu Thành', 'Cứ vào ngày 10/6 dương lịch hàng năm, tại Tháp 4 sư, nằm ở xã Vĩnh Hòa Hiệp, huyện Châu Thành các vị sư sãi ở các nơi trong tỉnh tập trung về tiến hành làm lễ cầu siêu cho 4 vị sư người Khmer: Danh Hùng, Danh Hom, Danh Tấp, Danh Hoi. Bốn vị sư đã hy sinh ', '10/6 dương lịch', '19', '1');
INSERT INTO `festivals` VALUES ('58', 'Lễ hội giỗ Tổ Hùng\n Vương – Tân Hiệp', 'Lễ hội Giỗ Tổ Hùng Vương ngày 10-3 âm lịch chính thức trở thành ngày Quốc lễ, ngày hội lớn quy tụ cả cộng đồng các dân tộc Việt Na m. Ở Kiên Giang, hàng năm cứ đến ngày 10/3, hàng chục ngàn người con đang sinh sống, học tập, làm việc ở mảnh đất Kiên Giang', '10/3 âm lịch', '19', '1');
INSERT INTO `festivals` VALUES ('59', 'Lễ hội giỗ đức khai \ntrấn Mạc Cửu – Hà Tiên', 'Đã trở thành truyền thống hàng năm, Hà Tiên đều tổ chức lễ hội giỗ đức khai trấn Mạc Cửu, là người có công đầu trong việc khai phá nên vùng đất Hà Tiên cách đây đã hơn 300 năm. Vào đầu thế kỷ thứ 18, Mạc Cửu trên đường tha hương lập nghiệp, ông đã đặt châ', '25-27/5 âm lịch', '19', '1');
INSERT INTO `festivals` VALUES ('60', 'Festival hoa Đà Lạt\n – Lễ hội hấp dẫn nhất ở Đà Lạt', 'Thời gian: Lễ hội được tổ chức 2 năm 1 lần vào dịp cuối năm.\n Địa điểm: Trung tâm thành phố Đà Lạt\nTừ năm 2005 nhằm tôn vinh người trồng hoa, nên cứ 2 năm một lần người dân ở đây lại tổ chức một lễ hội vô cùng long trọng “festival Hoa Đà Lạt” vào dịp cuối', '2 năm 1 lần vào dịp cuối năm', '22', '1');
INSERT INTO `festivals` VALUES ('61', 'Lễ hội Trà – Một lễ hội\n độc đáo ở Đà Lạt', 'Thời gian: Thường được tổ chức vào tháng 12 của năm.\nĐịa điểm: Không gian lễ hội mở rộng tổ chức ở cả 3 địa bàn còn lại làm nghề trà là huyện Bảo Lâm, Di Linh, và Cầu Đất (Tp Đà Lạt).\nLà một trong những lễ hội lớn và độc đáo ở Đà Lạt, lễ hội trà được tổ c', 'tháng 12', '22', '1');
INSERT INTO `festivals` VALUES ('62', 'Lễ Hội Cồng Chiêng – \nLễ hội văn hóa đậm chất ở Đà Lạt', 'Thời gian: Lễ hội cồng chiêng là một lễ hội được tổ chức hàng năm theo hình thức luân phiên.\nĐịa điểm: Tại các tỉnh có văn hóa cồng chiêng, trong đó có Lâm Đồng.\nLễ hội văn hóa cồng chiêng Tây Nguyên đã được UNESCO công nhận là di sản truyền khẩu và phi v', 'hàng năm theo hình thức luân phiên', '22', '1');
INSERT INTO `foods` VALUES ('1', 'Cá Kho', '1', 'Cồn Phụng, Cồn Quy, Lan Vương', 'Là một món đặc sản cầu kỳ của quê hương Bến Tre, được nấu từ gạo non với dừa xiêm.', 'cakho.jpg');
INSERT INTO `foods` VALUES ('2', 'Canh Chua', '1', 'xã Mỹ Thạnh và Hưng Nhượng huyện Giồng Trôm, TP.Bến Tre', 'Vừa là đặc sản đồng thời cũng là niềm tự hào của người dân xứ dừa.', '');
INSERT INTO `foods` VALUES ('3', 'Gà Hầm', '0', '\"bờ hồ Trúc Giang, thành phố Bến Tre hoặc', 'Món ăn vặt này không chỉ là món khoái khẩu của cô cậu thích ăn vặt mà còn là nỗi nhớ nhung của người xa quê.', '');
INSERT INTO `foods` VALUES ('4', 'Heo Quay', '0', '311B, đại lộ Đồng Khởi, phường Phú Khương, TP.Bến Tre.\"', 'Vị ngọt, giòn của thịt ốc hòa lẫn vị béo, thơm của bột gạo tạo thành món ăn dân dã thu hút du khách gần xa.', '');
INSERT INTO `foods` VALUES ('5', 'Cơm Hến', '0', 'cồn Phú Đa, huyện Chợ Lách, TP.Bến Tre', 'Bánh canh bột xắt có màu đục đặc trưng là do chất nhừ tiết ra từ bột. Còn thịt vịt trước khi nấu sẽ được ướp gia vị như hành, tiêu, muối cho vừa ăn, để một lúc cho thấm rồi đem xào săn.', '');
INSERT INTO `foods` VALUES ('6', 'Cơm Chiên', '0', 'ven Quốc lộ 60, xã Tân Thành Bình, huyện Mỏ Cày Bắc, TP.Bến Tre hoặc dưới chân cầu Cá Lóc, phường 8, TP.Bến Tre.', 'Chuột dừa Bến Tre xuất phát điểm là một trong các món nhậu tuy nhiên với độ thơm ngon của mình, chuột dừa Bến Tre đã trở thành đặc sản mà ai ai ở miền Tây đều muốn một lần nếm qua.', '');
INSERT INTO `foods` VALUES ('7', 'Mực Xào', '0', '\"Nhà hàng nổi Bến Tre: công viên Hùng Vương, phường 5, TP.Bến Tre hoặc Ninh Kiều 5: Khu phố Bình Khởi, phường 6, TP.Bến Tre', 'Đuông dừa thực chất là sâu dừa, những con sâu béo múp, có màu trắng và cách tuyệt vời nhất là ăn sống chấm kèm với mắm ớt.', '');
INSERT INTO `foods` VALUES ('8', 'Gà Chiên', '1', '\"', 'Về xứ ruộng mà không ăn cua đồng thì coi như chưa về ruộng.', '');
INSERT INTO `foods` VALUES ('9', 'Tôm Chiên', '1', 'Nhà hàng nổi Bến Tre: công viên Hùng Vương, phường 5, TP.Bến Tre;', 'Hương thơm dịu, vị béo ngậy, ngọt thanh thấm đượm tình người của kẹo dừa Bến Tre đã làm lưu luyến bao trái tim yêu dừa, nhớ đất Bến Tre.', '');
INSERT INTO `foods` VALUES ('10', 'Lẩu Chay', '1', 'Nhà hàng nổi Bến Tre: công viên Hùng Vương, phường 5, TP.Bến Tre;', 'Củ hũ dừa là phần đọ non trắng béo của cây dừa, chứa rất nhiều chất dinh dưỡng, muốn có phải chặt cả cây dừa.', '');
INSERT INTO `foods` VALUES ('11', 'Cá Lóc Nướng Truôi', '0', 'Cửa hàng kẹo dừa Thanh Long, số 72B1, 495b đại lộ Đồng Khởi, phường Phú Khương, TP.Bến Tre', 'Tô bánh canh đặc sản Tây Ninh đặc trưng với giò heo, huyết và những cọng hành xanh tươi, tuy đơn giản nhưng thực khách ngay lập tức bị cuống hút khi thưởng thức qua', '');
INSERT INTO `foods` VALUES ('12', 'Bò Kho', '0', 'Tỉnh lộ 887 TP.Bến Tre', 'Bánh tráng phơi sương thường ăn kèm với rau sống, thịt heo luộc và chấm với nước mắm pha chế công phu.', '');
INSERT INTO `foods` VALUES ('13', 'Món ăn chay Tây\nNinh', '1', '88 Nguyễn Văn Rốp, Trảng Bàng, Tây Ninh.', 'Theo tục lệ xưa, tháng giêng là tháng ăn chay đối với người theo đạo Cao Đài. Phần đông dân cư Tây Ninh là tín đồ đạo Cao Đài nên tháng ăn chay và nghề nấu món chay rất được coi trọng và nổi tiếng.', '');
INSERT INTO `foods` VALUES ('14', 'Bờ tơ Củ Chi', '1', 'ấp Lộc Du, thị trấn Trảng Bàng, tỉnh Tây Ninh', 'Bò tơ Củ Chi từ lâu đã trở thành niềm tự hào của người dân vùng đất này. Giống bò được chăn thả với số lượng lớn trên những cánh đồng cỏ nơi đây mang đến hương vị đầy khác biệt.', '');
INSERT INTO `foods` VALUES ('15', 'Mắm chua Tây Ninh', '1', '240 Lý Thường Kiệt, Hòa Thành, Tây Ninh.', 'Món ăn đặc sắc khác của cư dân Tây Ninh mà chỉ những người dân Nam Bộ lâu năm mới quen ăn và mê nhất, đó là mắm chua.', '');
INSERT INTO `foods` VALUES ('16', 'Ốc xu núi Bà', '1', 'Tỉnh Lộ 6, Hưng Thịnh, Trảng Bàng, Tây Ninh', 'Có hình dạng gần giống ốc bươu nhưng có mình dẹt và nhỏ hơn, người dân nơi đây cho rằng ốc xu núi Bà không những ngon đặc trưng mà còn có tác dụng chữa nhức mỏi khá tốt.', '');
INSERT INTO `foods` VALUES ('17', 'Thằn lằn núi Bà Đen', '1', 'phường 3, tỉnh Tây Ninh', 'Được xem là đệ nhất ẩm thực ở xứ Tây Ninh, thằn lằn núi là món nhậu cuốn hút cánh mày râu. Món ăn này luôn được nhiều thực khách săn đón khi đến Tây Ninh.', '');
INSERT INTO `foods` VALUES ('18', 'Muối Tôm', '1', 'núi Bà', 'Nhắn đến Tây Ninh không thể bỏ qua món muối tôm, đặc sản độc đáo của địa phương. Công dụng của muối dùng để chấm trái cây nhưng lại thu hút rất nhiều thực khách. ', '');
INSERT INTO `foods` VALUES ('19', 'Nem Bưởi', '1', 'núi Bà', 'Nem bưởi là món ăn chay phổ biến của người dân Tây Ninh. Nem bưởi có vị chua, mặn, ngọt, vị cay của ớt, tiêu và một hương thơm đặc trưng làm thành món nem lạ lẫm và đầy dư vị hấp dẫn.', '');
INSERT INTO `foods` VALUES ('20', 'Bánh tráng me', '1', 'Số 11 Đồng Tiến, khu phố Lộc Du, Trảng bàng, Tây Ninh.', 'Trong hàng chục món bánh tráng thuộc về nhóm quà vặt xuất xứ từ Tây Ninh, bánh tráng me thuộc loại có sức hấp dẫn không cưỡng được nếu đã một lần nếm qua.', '');
INSERT INTO `foods` VALUES ('21', 'Mầm đá', '1', 'ấp Hiệp Nghĩa, xã Hiệp Ninh, thị xã Tây Ninh', 'Món ăn này không chỉ có trong truyện cổ Trạng Quỳnh dâng vua mà thực sự tồn tại ngoài đời thực. Chỉ khác là thứ rau lạ này không tốn nhiều thời gian chế biến, thậm chí chỉ cần chần sơ qua nước sôi là có thể ăn được.', '');
INSERT INTO `foods` VALUES ('22', 'Cuốn sủi', '1', 'Ô2/42AB khu phố Rạch Sơn, thị trấn Gò Dầu, huyện Gò Dầu, tỉnh Tây Ninh.', 'Đây là món ăn độc đáo của Lào Cai, còn được biết với tên gọi là phở khan. Cuốn sủi bao gồm bánh phở, củ dong rang giòn, thịt bò, rau thơm, lạc rang, hạt tiêu, ớt và nước sốt đặc biệt chế biến từ nhiều loại gia vị. ', '');
INSERT INTO `foods` VALUES ('23', 'Thắng cố', '1', 'thị trấn SaPa', 'Thắng cố không phải là món ăn bắt mắt, thậm chí hương vị cũng khiến nhiều du khách e ngại. Tuy vậy, ai lên Sa Pa mà không ăn thắng cố thì xem như chưa thực sự tìm hiểu về ẩm thực phố núi.', '');
INSERT INTO `foods` VALUES ('24', 'Lẩu cá hồi', '1', '45 Ngô Quyền, Kim Tân, Thành Phố Lào Cai, Lào Cai', 'Từng thớ cá săn chắc, ngọt thịt, màu hồng cam đẹp mắt kết hợp với măng chua, lá giang, bông bí, hoa chuối… trong món lẩu thơm ngon khiến du khách thích thú khi đến thăm vùng đất này.', '');
INSERT INTO `foods` VALUES ('25', 'Thịt gừng', '1', 'Bắc Hà, Mường Khương, Sa Pa - Lào Cai.', 'Món ăn truyền thống của người Nùng Dín được làm từ các loại xương sườn, xương sống và thủ lợn', '');
INSERT INTO `foods` VALUES ('26', 'Thịt lợn cắp nách', '1', 'SaPa', 'Lợn được nuôi thả rong nên sức đề kháng tốt, ăn cây củ dại hàng ngày nên thịt ngọt tự nhiên, không lẫn với các loại thịt khác.', '');
INSERT INTO `foods` VALUES ('27', 'Đồ nướng', '1', 'Bắc Hà, SaPa', 'Tiết trời buổi tối ở Sa Pa khiến người ta muốn ngồi bên lửa than và chờ đợi những xiên thịt chín đều. Thịt lợn cắp nách, bò cuốn cải mèo, chả cá hồi, cơm lam, trứng nướng đều tỏa mùi thơm ngào ngạt, kích thích vị giác. ', '');
INSERT INTO `foods` VALUES ('28', 'Rau cải mèo', '1', 'SaPa', 'Cải mèo lá xoăn, màu xanh sẫm, giòn và ngọt hơn cải thường. Mỗi mâm cơm của người dân phố núi Sa Pa ngoài những món thịt thì không thể thiếu đĩa rau cải mèo xào với mỡ đông, thoảng chút cay nồng của gừng tươi.', '');
INSERT INTO `foods` VALUES ('29', 'Gà đen', '1', 'SaPa', 'Gà đen còn được gọi là gà ác, có phần da màu đen, xương giòn, thịt thơm ngọt. Ở Sa Pa, món ăn nổi tiếng nhất được chế biến từ nguyên liệu này là gà đen nướng mật ong.', '');
INSERT INTO `foods` VALUES ('30', 'Thịt trâu gác bếp', '1', 'SaPa', 'Món ăn này vốn dĩ được chế biến bởi người Thái đen, nhằm dự trữ thực phẩm cho mùa mưa lũ hoặc những chuyến đi rừng dài ngày. Những mảng thịt thăn, thịt bắp được cắt dọc, ướp gia vị, lá rừng rồi treo lên gác bếp hun khói.', '');
INSERT INTO `foods` VALUES ('31', 'Răng mực nướng', '1', 'SaPa', 'Nếu như trước đây người ta coi răng mực là đồ bỏ đi thì nay với bàn tay chế biến khéo léo thì nó đã trở thành một món ăn đặc sản tại Phan Thiết rất thơm ngon, hấp dẫn.', '');
INSERT INTO `foods` VALUES ('32', 'Bánh hỏi lòng heo Phú Long', '1', 'SaPa', 'Mặc dù loại bánh này ở đâu cũng có xong bánh hỏi lòng heo tại Phú Long vẫn ngon và đặc biệt nhất. Món bánh được làm bởi những nguyên liệu vô cùng gần gũi là bánh hỏi, bánh tráng mỏng, lòng heo, nước chấm và rau sống.', '');
INSERT INTO `foods` VALUES ('33', 'Gỏi cá', '1', '202, Võ Thị Sáu, Hưng Long, Tp. Phan Thiết, Bình Thuận', 'Phan Thiết được biết tới là một vùng đất của nắng, giớ và biển cùng những loài hải sản tươi ngon. Chính vì thế mà món gỏi cá Phan Thiết không hề thua kém gì so với những nơi khác', '');
INSERT INTO `foods` VALUES ('34', 'Bánh căn Phan Thiết', '1', 'Quốc Lộ 1 - Quán ăn - Miền Trung tại Xã Hàm Nhơn, Huyện Hàm Thuận Bắc, Hàm Thuận Bắc, Bình Thuận.', 'Bánh căn là một món ăn đặc sản của Phan Thiết được bán tại nhiều nơi và nhất là buổi chiều. Cùng với vị thơm ngon, và vị đậm đà của nước chấm đem tới chắc chắn nếu như ai đã từng được nếm thử món ăn đặc biệt này sẽ nhớ mãi không quên.', '');
INSERT INTO `foods` VALUES ('35', 'Dông 7 món', '1', 'Phan Thiết', 'Nếu như hỏi nên ăn gì khu du lịch Mũi Né – Phan Thiết thì chắc chắn câu trả lời sẽ là những món ăn hải sản hấp dẫn từ dông. Đây là một loại bò sát thường sống ở trong cát cho thịt dai, chắc và ngọt.', '');
INSERT INTO `foods` VALUES ('36', 'Bánh tráng chấm mắm ruốc', '1', '168 Thủ Khoa Huân, Tp. Phan Thiết, Bình Thuận.', 'Có thể khẳng định đây chính là món ăn đặc trưng của Phan Thiết thường được gắn liền cùng với tuổi thơ của phần lớn người dân sinh sống tại đây.', '');
INSERT INTO `foods` VALUES ('37', 'Bánh canh chả cá', '1', 'Phan Thiết', 'Phần lớn những du khách khi đến với Phan Thiết thì đều thưởng thức qua món ăn này, mặc dù đơn giản song nó lại có một sức hút rất đặc biệt.', '');
INSERT INTO `foods` VALUES ('38', 'Cá lồi xối mỡ', '1', 'Phan Thiết', 'Cá lồi xối mỡ là món ăn thơm ngon và dinh dưỡng của Phan Thiết thường được chế biến từ nguyên liệu chính là cá lồi.', '');
INSERT INTO `foods` VALUES ('39', 'Bún bò Phan Thiết', '1', 'Phan Thiết', 'Có lẽ khi nhắc tới món bún bò nhiều người sẽ liên tưởng ngay tới món bún bò Huế nhưng món bún bò ại Phan Thiết cũng rất đặc biệt và có mùi vị thơm ngon hấp dẫn, đặc trưng của nó.', '');
INSERT INTO `foods` VALUES ('40', 'Lẩu thả', '1', 'Phan Thiết', 'Lẩu thả chính là món lẩu đặc sản của Phan Thiết. Nếu như đến với nơi đây thì bạn đừng bỏ qua món ăn tuyệt cư mèo này nhé! Bởi lẽ món ăn này đã chinh phục được rất nhiều du khách khi thưởng thức. ', '');
INSERT INTO `foods` VALUES ('41', 'Cơm Hến', '1', 'Võ Thị Sáu, Tp. Phan Thiết, Bình Thuận.', 'Cơm hến tuy là món ăn dân dã có khắp mọi nơi dù ở thôn xóm hay đường quê, nghèo mà vẫn sang, đậm đà hương vị.', '');
INSERT INTO `foods` VALUES ('42', 'Bánh canh Bà Đợi', '1', 'Mũi Né, Phan Thiết.', 'Dù bánh canh của quán này được thái sợi dẹt như kiểu Quảng Bình chứ không nén khuôn sợi tròn, nhưng nước dùng thì đặc phong cách Huế.', '');
INSERT INTO `foods` VALUES ('43', 'Chè Hẻm', '1', 'đường Phạm Hồng Thái, góc giao với Trương Định', 'Không ai biết chè hẻm có ở Huế từ bao giờ mà chỉ biết gọi là thế, bởi nó thường nằm sâu trong các ngõ ngách với rất nhiều loại chè khác nhau.', '');
INSERT INTO `foods` VALUES ('44', 'Cơm chay Huế', '1', 'đường Đào Duy Anh', 'Nếu bạn muốn có một bữa ăn thanh đạm và để cơ thể được thanh lọc thì hãy thử một bữa cơm chay tại Huế.', '');
INSERT INTO `foods` VALUES ('45', 'Bún bò Huế', '1', 'Số 1 kiệt 76 Hùng Vương, tổ 7, Phú Nhuận, Thành phố Huế', 'Bún bò Huế có một miếng chân giò, một miếng giò tự nắm, một miếng tiết lợn nhỏ và tất nhiên phải có vài lát thịt bò. Rau ăn kèm cũng rất tươi và phong phú.', '');
INSERT INTO `foods` VALUES ('46', 'Bún thịt nướng, bánh ướt thịt nướng', '1', 'chùa Từ Đàm', 'Điểm đặc biệt của hai món này nằm ở thịt nướng. Thịt ở đây ướp vừa đủ, không át mùi thơm của, miếng thịt mềm chứ không bị khô, và mang một hương vị đặc trưng, khá đặc biệt so với những nơi khác.', '');
INSERT INTO `foods` VALUES ('47', 'Các loại bánh Huế: Bánh bèo, bánh bột lọc, bánh khoái', '1', '3 Lý Thường Kiệt (cạnh Nhà khách Công đoàn)', 'Tận dụng nguồn hải sản vô tận của hệ thống đầm phá Tam Giang - Cầu Hai kết hợp với các loại bột cơ bản nhất như bột gạo, bột năng, bột nếp, người Huế đã tạo ra các món bánh nức lòng thực khách', '');
INSERT INTO `foods` VALUES ('48', 'Bánh chưng Nhật Lệ', '1', 'mạn Kim Long – đường đi chùa Thiên Mụ', 'Đây là món ăn nổi tiếng ở Huế và có xuất xứ từ con phố Nhật Lệ trong thành Nội, nơi tập trung hàng chục lò làm bánh.', '');
INSERT INTO `foods` VALUES ('49', 'Nem lụi chất Huế', '1', 'cung An Định, đường Ngự Bình, đường Nguyễn Bỉnh Khiêm', 'Khách hàng lần đầu ăn nem lụi, ai cũng xuýt xoa khen ngon để rồi ăn tiếp lần hai, lần ba, thậm chí ăn hàng ngày như dân “nghiện\" và lần nào cũng vẫn cứ khen ngon.', '');
INSERT INTO `foods` VALUES ('50', 'Chip chip hấp sả Đà Nẵng', '1', '117 Nhật Lệ, Thuận Thành, Tp. Huế', '\"Chip chip rửa sạch bụi đất, sả tươi thái nhỏ, đập dập, ớt đỏ thái miếng và cho vào nồi hấp chung. Bạn chỉ phải chờ chừng năm phút là có nồi hải sản thơm lừng, đầy hấp dẫn. Cách ăn đúng điệu là chấm cùng muối tiêu chanh ớt, nhờ đó mới cảm nhận hết vị ngọt thanh trong hương thơm nhẹ của sả và cay nồng từ  tiêu ớt.', '');
INSERT INTO `foods` VALUES ('51', 'Cua rang me Đà Nẵng', '1', 'đường Nguyễn Huệ', '', '');
INSERT INTO `foods` VALUES ('52', 'Ốc hút Đà Nẵng', '1', 'dọc các đường bờ biển, các quán nhậu bình dân bạn dễ dàng tìm thấy món ăn hấp dẫn này', 'Một tô chíp chíp có giá từ 30.000 – 50.000 đồng.\"', '');
INSERT INTO `foods` VALUES ('53', 'Cháo hàu Đà Nẵng', '1', ' Bạn có thể thưởng thức món ăn ngon tuyệt này tại bất cứ nhà hàng hải sản nào ở Đà Nẵng', '\"Cua rang me có cách chế biến khá cầu kỳ. Cụ thể, cua rửa sạch, tách mai, lấy gạch để riêng sau đó ướp gia vị rồi chiên vàng. Me bóp nhuyễn bỏ hạt và đun cùng tỏi phi, hành tây, gia vị. Để hút khách, mỗi hàng lại có những bí quyết riêng. Sau chế biến, cua có màu đỏ bắt mắt cùng hương thơm ngào ngạt quyến rũ. Bạn hãy gỡ từng miếng thịt rồi chấm vào nước sốt me sền sệt chua ngọt để cảm nhận đủ vị chua cay mặn ngọt. ', '');
INSERT INTO `foods` VALUES ('54', 'Mực nướng sa tế Đà Nẵng', '1', '\"Quán ốc hút nằm trong chợ Bắc Mĩ An: 7k/đĩa', '', '');
INSERT INTO `foods` VALUES ('55', 'Sò điệp nướng mỡ hành Đà Nẵng', '1', 'Quán ốc hút đối diện nhà thi đấu đĩa bay trên đường 2/9: 20-25k/đĩa', 'Cua rang me có giá khoảng 70k/con\"', '');
INSERT INTO `foods` VALUES ('56', 'Lẩu hải sản Đà Nẵng', '1', 'Quán ốc hút cây bàng 59 Ông Ích Đường, quận Cẩm Lệ, TP Đà Nẵng (gần THPT Hòa Vang,) mở cửa từ 5h chiều đến 10h đêm: 25k/đĩa\"', 'Nói tới Đà Nẵng không thể không kể đến ốc hút, món ăn “ruột” của dân địa phương nơi đây. Không chỉ thế, chỉ với một dĩa ốc hút và một dĩa mít trộn, du khách có thể vừa thưởng thức, vừa trò chuyện với bạn bè và ngắm đường phố Đà Nẵng', '');
INSERT INTO `foods` VALUES ('57', 'Dê núi Ninh Bình', '1', 'Bạn có thể thưởng thức món cháo này ở bất cứ quán hải sản nào tại Đà Nẵng.', '\"Hàu tươi rửa sạch, nêm nếm gia vị rồi đun cùng cháo trắng đã ninh từ trước. Khi có khách, chủ quán sẽ cho chút ngò, tỏi phi, rau mùi và rắc tiêu thơm lên trên. Ngoài ra, bạn đừng quên rưới thêm nước mắm nhĩ vào tô cháo còn nghi ngút khói giúp món ăn đậm vị hơn.', '');
INSERT INTO `foods` VALUES ('58', 'Cơm cháy Ninh Bình', '1', 'Bạn có thể thưởng thức món cháo này ở bất cứ quán hải sản nào tại Đà Nẵng.', '', '');
INSERT INTO `foods` VALUES ('59', 'Rượu Kim Sơn Ninh Bình', '1', 'Bạn có thể thưởng thức món cháo này ở bất cứ quán hải sản nào tại Đà Nẵng.', 'Giá một tô cháo hàu khoảng 60.000 đồng, đủ cho khoảng 2 – 3 người ăn.\"', '');
INSERT INTO `foods` VALUES ('60', 'Miến lươn Ninh Bình', '1', '\"Hải Sản Bé Mặn', '\"Món này có cách chế biến khá đơn giản. Mực mua về làm sạch rồi ướp muối, tiêu, tỏi, đường, dầu mè, sa tế sau đó nướng trên than hồng. Khi ăn, chủ quán sẽ dọn thêm đĩa rau sống như xà lách, hành, thì là cùng chén muối tiêu hoặc tương ớt.', '');
INSERT INTO `foods` VALUES ('61', 'Canh chua cá rô Ninh Bình', '1', 'Địa chỉ: Lô 14 Hoàng Sa, Quận Sơn Trà, Đà Nẵng, Việt Nam', '', '');
INSERT INTO `foods` VALUES ('62', 'Nem Yên Mạc Ninh Bình', '1', '0905 207 848 – 0905 181 529', 'Mực nướng sa tế có giá khoảng 100.000 đồng/ đĩa.\"', '');
INSERT INTO `foods` VALUES ('63', 'Gỏi cá nhệch Kim Sơn Ninh Bình', '1', '', '\"Những con sò điệp lớn rửa sạch sau đó tách làm đôi. Lúc này chủ quán mới khéo léo ướp các loại gia vị như tiêu, hạt nêm, đường... rồi đem nướng trên than hoa. Khi chín, món này tỏa ra mùi thơm ngào ngạt cùng phần nước ngọt đậm tự nhiên. Thêm hành lá và lạc giã nhỏ, bạn sẽ cảm nhận được trọn vẹn vị ngọt dai và bùi bùi hấp dẫn. Ảnh: Tiêu Phong.', '');
INSERT INTO `foods` VALUES ('64', 'Ốc núi Ninh Bình', '1', 'Quán hải sản KỲ EM', '', '');
INSERT INTO `foods` VALUES ('65', 'Cốm Hà Nội', '1', 'Địa chỉ: 179 Hồ Nghinh – Q.Sơn Trà – TP.Đà Nẵng.', 'Một đĩa sò điệp nướng có giá khoảng 70.000 – 100.000 đồng.\"', '');
INSERT INTO `foods` VALUES ('66', 'Bánh tôm Hà Nội', '1', '', 'Nhiều thực khách sau khi thưởng thức lẩu hải sản ở Đà Nẵng thường để lại những nhận xét ưu ái. Một trong số đó là lời khen cho phần nước lẩu đậm, ngon, các loại hải sản tươi sống và rau ăn kèm đa dạng. ', '');
INSERT INTO `foods` VALUES ('67', 'Chả cá Lã Vọng Hà Nội', '1', 'Quán Hải Sản Luyện', 'Ninh Bình có địa hình chủ yếu là đồi núi đá, chính vì vậy mà thuận lợi cho việc nuôi dê. Dê núi Ninh Bình thường được nuôi trên ', '');
INSERT INTO `foods` VALUES ('68', 'Phở bò Hà Nội', '1', 'Địa chỉ: 09 Thanh Huy 1 – Q.Thanh Khê – TP.Đà Nẵng', '', '');
INSERT INTO `foods` VALUES ('69', 'Bún thang Hà Nội', '1', '', 'Là huyện duy nhất của Ninh Bình tiếp giáp với Biển. Kim Sơn không những được bồi đắp phù sa mà còn có một dòng nước trong lành. Kim Sơn là một trong những vựa gạo lớn nhất miền bắc sau Thái Bình và Nam Định. hạt gạo ở Kim Sơn ăn rất ngon, thơm, dẻo nên tạo ra những giọt rượu thật đậm đà. Cùng với sự khéo léo của người Kim Sơn, một đặc sản rượu Kim Sơn ra đời và là hình ảnh đại diện cho đặc sản Ninh Bình. Rượu Kim Sơn thường được dùng khi ăn với thịt dê và cơm cháy.', '');
INSERT INTO `foods` VALUES ('70', 'Bún chả Hà Nội', '1', 'Nhà Hàng Mỹ Hạnh', 'Cùng với cơm cháy, tái dê, miến lươn cũng là món đặc sản nổi tiếng ở Ninh Bình.', '');
INSERT INTO `foods` VALUES ('71', 'Bún ốc Hà Nội', '1', 'Địa chỉ: Lô 18 Hoàng Sa, P. Phước Mỹ, Quận Sơn Trà, Đà Nẵng, Việt Nam', ' Cá rô đư­ợc chế biến để nấu món canh chua, đây là món ăn đặc sản của vùng núi đá vôi ven đồng chiêm trũng. Món canh chua này khi chế biến không thể thiếu nước dưa chua để khử mùi tanh và tạo nên vị thanh thanh hấp dẫn. Món ăn này có vị ngọt ngọt, chua chua của n­ước dư­a cải, ngọt mát của cà chua, đậu phụ, ngậy, bùi, giòn và thơm của cá rô…', '');
INSERT INTO `foods` VALUES ('72', 'Lẩu mắm Sài Gòn', '1', 'ĐT: (0511) 3 831 494', 'Nem Yên Mạc do được tinh chế khá công phu, sợi nem nhỏ, đỏ hồng, rời, tươi ướp với gia vị và lá ổi tàu để được hàng tuần. Nem Yên Mạc phải ăn kèm với chút lá ổi, lá sung, cùng rau thơm cuộn lại, chấm với nước mắm chanh, cho thêm một ít ớt, tỏi và hạt tiêu. Như vậy người ăn mới cảm nhận đủ vị ngọt, cay của hương vị đặc biệt của món ăn này.', '');
INSERT INTO `foods` VALUES ('73', 'Bún mắm Sài Gòn', '1', '', 'Để chế biến ra món ăn đặc sắc gỏi cá Nhệch này cần một chuỗi nhiều khâu hết sức kì công. Món ăn này mang hương thơm bùi bùi của gạo nếp rang, vị chua thanh thanh của dấm xen vào cái vị cay ấm của gừng với tỏi, ớt, tiêu, sả. Gỏi cá nhệch thơm và bùi, có mùi vị rất đặc trưng, ăn một lần là nhớ mãi không quên', '');
INSERT INTO `foods` VALUES ('74', 'Lẩu cá kèo Sài Gòn', '1', 'Nhà hàng 4U Biển', 'Thức ăn chính của ốc này là những loại cây cỏ mọc hoang trên núi trong đó có cả những cây thuốc quý. Vì vậy khi sơ chế người ta chỉ rửa qua vì cho rằng trong người con ốc mang nhiều vị thuốc. Thịt ốc núi dai, giòn, ngọt. Ốc núi có thể chế biến thành nhiều món: hấp gừng, xào sả ớt, trộn gỏi…', '');
INSERT INTO `foods` VALUES ('75', 'Mì hủ tiếu hoành thánh Sài Gòn', '1', 'Địa chỉ: Lô 9 +10 Đường Trường Sa – Biển Mỹ Khê – Tp.Đà Nẵng', 'Cốm không được gói trong giấy báo hay đựng trong bát mà chỉ ngon khi gói vuông vắn trong những chiếc lá sen, bên ngoài buộc bằng dây rơm nếp. Mùi hương của cốm mới quyện cùng mùi thơm nồng nồng, man mát của lá sen khiến hương cốm càng trở nên tinh tế.', '');
INSERT INTO `foods` VALUES ('76', 'Bánh tráng Trảng Bàng Sài Gòn', '1', '', 'Bánh tôm gồm tôm nước ngọt bọc một lớp bột mỳ rồi cho vào chảo mỡ nóng già ngập bánh. Khi bánh phồng lên và ngã màu vàng, nghe mùi thơm ngậy hấp dẫn là ăn được. Chiếc bánh tôm nhỏ bằng lòng bàn tay, ở giữa là một con tôm vừa vặn, đỏ rực bắt mắt. Ở Hà Nội, người ta ăn bánh tôm với rau sống và nước chấm gồm dấm chua cay ngọt, thêm chút dưa góp cho đỡ ngấy. Nếu may mắn, bạn sẽ được thưởng thức mùi hương cà cuống trong nước chấm, mang tới cảm giác thích thú, khó quên', '');
INSERT INTO `foods` VALUES ('77', 'Bò tơ Củ Chi Sài Gòn', '1', 'Hải sản Đà Nẵng – Nhà hàng Bầu Trời Đỏ', 'Món chả cá Lã Vọng nổi tiếng ở Hà Nội luôn nằm trong top những món ăn ngon không nên bỏ lỡ được giới thiệu trong các cuốn sách về du lịch Việt Nam. Chả cá Lã Vọng ngon là sự kết hợp hoàn hảo giữa các gia vị đặc trưng Việt Nam như nghệ, thì là, mắm tôm và nước mắm, tất cả hòa quyện lại thành một món ăn có mùi thơm vô cùng hấp dẫn.Món này được ăn kèm với ớt đỏ, bún, các loại rau thơm, rưới lên trên là những miếng cá rán ngon tuyệt.', '');
INSERT INTO `foods` VALUES ('78', 'Bánh xèo Nam bộ Sài Gòn', '1', 'Địa chỉ: Lô 2 đường Hoàng Sa – Sơn Trà – Đà Nẵng', 'Phở là một thứ quà đặc biệt của Hà Nội, không phải chỉ riêng Hà Nội mới có, nhưng chính là vì chỉ ở Hà Nội mới ngon. Phở ngon phải là phở \"cổ điển\", nấu bằng thịt bò, nước dùng trong và ngọt, bánh dẻo mà không nát, thịt mỡ gầu giòn chứ không dai, chanh ớt với hành tây đủ cả, rau thơm tươi, hồ tiêu bắc, giọt chanh cốm gắt, lại điểm thêm một ít cà cuống, thoảng nhẹ như một nghi ngờ\"', '');
INSERT INTO `foods` VALUES ('79', 'Gỏi cuốn Sài Gòn', '1', 'Điện thoại: 0905.72.75.76', 'Có tới gần 20 nguyên liệu mới đủ làm nên món ăn nhiều màu sắc và đủ mùi vị tạo nên một bát bún thang ấn tượng trong lòng thực khách: trứng tráng mỏng thái chỉ, gà xé nhỏ, giò lụa thái sợi, rau răm, nấm hương, củ cải ngâm… Nước dùng trong và ngọt thơm, đậm đà vị của tôm, chan đều lên bát bún nhỏ xinh vừa ăn', '');
INSERT INTO `foods` VALUES ('80', 'Các món ốc  Sài Gòn', '1', '\"', 'Dưới cái nắng hè oi ả, dù bát bún có ngon tới đâu mà bốc khói nghi ngút thì ắt hẳn cũng sẽ bớt đi phần nào hấp dẫn. Do đó, người Hà Nội thường tìm tới món ăn không chan nước dùng nóng, nhiều đạm nhưng không ngấy này. Mùi thơm từ những vỉ nướng chả trở luôn tay trên bếp than chính là biển hiệu quyến rũ nhất của các quán bún chả. Khi ăn bún chả, thực khách có thể chọn giữa chả viên và chả nướng, mỗi loại ngon một kiểu khác nhau. Nếu như chả viên là sự kết hợp của hành tỏi, thịt băm nhỏ tẩm ướp nên khá mềm và ngọt thì chả miếng lại dai dai, thơm thơm mùi mỡ cháy. Nước chấm cũng là bí quyết riêng của mỗi quán bởi nước chấm dở sẽ khó lòng tạo nên bát bún ngon được.', '');
INSERT INTO `foods` VALUES ('81', 'Các món chè Sài Gòn', '1', 'Giá thịt dê khoảng: 300,000 – 400,000 đ/kg, nếu ăn tại nhà hàng tại một đĩa dê giao động từ 80,000 – 150,000 đ/ đĩa tùy vào các món khác nhau. Với đặc sản thịt dê núi Ninh Bình người ta có thể chế biến ra các món như là; Dê xào lăn, dê nướng, tiết canh dê, dê hấp, lẩu dê, chân dê hầm thuốc bắc, dê áp chảo…', 'Món ăn dân dã, bắt nguồn từ đồng ruộng này không rõ xuất hiện ở Hà Nội từ bao giờ nhưng từ lâu đã trở thành món quà vặt được ưa thích số một. Người ta có thể ăn bún ốc cho bữa sáng, trưa hoặc tối, ăn vào bất cứ thời điểm nào. Tuy nhiên, thời khắc mà người Hà Nội nhớ tới và thèm bún ốc nhất là trong những ngày Tết cổ truyền. Bát bún ốc luôn sặc sỡ sắc màu của cà chua chưng, những con ốc béo mỡ màng, chuối đậu vàng ươm. Hương vị là sự kết hợp ăn ý của nước dùng trong veo nhưng không kém phần béo ngậy, chua chua thanh thanh và dậy mùi dấm bỗng đặc trưng. Khi ăn, thêm chút hoa chuối thái mỏng và rau sống tươi ngon mới khiến bát bún đầy đủ hương vị.', '');
INSERT INTO `foods` VALUES ('82', 'Ba Khía Rạch Gốc Sài Gòn', '1', 'Ngoài đặc sản thịt dê ra, Ninh Bình còn nổi tiếng với món cơm cháy. Sau khi nấu cơm bằng nồi gang, người đầu bếp lấy cháy ở đáy nồi và cho ra phơi nắng. Sau khi cháy đã khô, cho vào chảo dầu chiên cho đến lúc hạt cơm cháy vàng ương và có mùi thơm ngậy là được. Cơm cháy ngon nhất là ăn với nước sốt dê. Với vị ngậy của cháy hòa quyện vào vị ngọt và thơm của nước sốt dê, chắc chắn bạn sẽ nhớ mãi hương vị cơm cháy Ninh Bình.', 'Lẩu mắm ngon nhất thì phải về miền Tây (Cần Thơ, Vĩnh Long, Long An), nhưng nếu không có thời gian thì bạn có thể thưởng thức lẩu mắm ngay tại Sài Gòn. Nồi lẩu mắm quan trọng nhất là nước dùng, phải chế biến từ loại mắm ngon, nêm nếm khéo léo cho vừa ăn. Lẩu mắm ở Sài Gòn thường có rau muống, kèo nèo, bông súng, đậu bắp, bạc hà, bắp chuối, rau nhút.', '');
INSERT INTO `foods` VALUES ('83', 'Cá lóc nướng trui Cà Mau\n', '1', 'Hiện nay hầu hết các cửa hàng ở Ninh Bình đều có rượu Kim Sơn, tuy nhiên để ngon nhất thì bạn nên vào huyện Kim Sơn để thưởng thức là ngon nhất.', 'Nước dùng là thành phần quan trọng nhất của món bún mắm. Tô bún mắm ngon không quá ngọt, quá mặn hay cay nhưng cũng không hề nhạt nhẽo, nhất là không thể thiếu vị thơm nức của mắm. Ăn bún mắm ngoài lát cá, thịt heo quay, tôm, mực, không thể thiếu đĩa rau sống với rau đắng, bắp chuối, kèo nèo, cọng bông súng, húng thơm. Một số quán ăn ghi điểm thêm nhờ tô nước mắm me chua chua ngọt ngọt rất hấp dẫn.', '');
INSERT INTO `foods` VALUES ('84', 'Lẩu mắm U Minh Cà Mau', '1', 'Bạn có thể thưởng thức món ăn này tại 3 nhà hàng gần nhau ở ngã tư đường Trần Hưng Đạo, thành phố Ninh Bình, một ngả ra chợ Rồng, một ngả vào bệnh viện tỉnh. Hương thơm của món miến lươn lan toả ngọt ngào ra khắp một không gian rộng nơi đây như mời chào khiến du khách khó có thể bỏ qua mỗi khi có dịp tới Ninh Bình.', 'Lẩu cá kèo là món ăn mang hương vị miền Nam đặc trưng. Món lẩu từ cá kèo thường được nấu kèm với lá giang tạo nên vị chua chua, chát chát đặc trưng. Rau dùng với lẩu cá kèo gồm rau muống, rau nhút và rau đắng, giá, hoa chuối… Mang hương thơm hấp dẫn của rau và gia vị đặc trưng, nồi lẩu cá kèo bốc lên sẽ thơm lừng, khó quên.', '');
INSERT INTO `foods` VALUES ('85', 'Gỏi nhộng U Minh Cà Mau', '1', 'Tổng Tr­ường trư­ớc là tổng Trư­ờng Yên (nay là xã Trư­ờng Yên, huyện Hoa Lư), đây là vùng đất có nhiều hang động, có loại cá rô to và béo.', 'Những món ăn này tuy không phải nguồn gốc bản địa song giờ đây cũng trở thành nét đặc trưng của Sài Gòn. Với người dân Sài Gòn, món này dễ ăn và khá được ưa chuộng. Tuy nhiên với dân Bắc, món này có vị hơi ngọt, có thể không quen khi thưởng thức lần đầu. Các quán hủ tiếu rất sẵn trên bất kỳ còn đường, ngõ hẻm nào của Sài Gòn với giá từ 20.000VND/tô.', '');
INSERT INTO `foods` VALUES ('86', 'Chuột đồng Cà Mau', '1', 'Nem Yên Mạc được truyền lại từ đời nào không rõ, nhưng những người sành ăn ở đây đã biết đến từ lâu. Ngày nay, nem Yên Mạc đã vươn xa, có mặt ở nhiều khách sạn, nhà hàng cả trong và ngoài tỉnh.', 'Đây là món ăn của người Tây Ninh, ngoài bánh tráng phơi sương còn có bánh canh, bánh tráng được ăn với thịt heo luộc, thịt bò luộc, cùng với rất nhiều loại rau và mắm nêm đặc trưng của người miền Tây.', '');
INSERT INTO `foods` VALUES ('87', 'Tôm tít Cà Mau', '1', 'Trong tỉnh Ninh Bình có nhiều nơi giới thiệu món ăn này, nhưng gỏi cá nhệch ở Kim Sơn được coi là ngon nhất. ', 'Miếng thịt bò mềm ngọt được quấn với các loại rau tươi rói của Nam Bộ và bánh tráng, ăn với nước chấm chua ngọt pha rất khéo đã làm nên đặc sản bò tơ Củ Chi nổi tiếng. Hiện nay nhiều nhà hàng ở Sài Gòn đã phục vụ món nay nhưng thực khách vẫn chưa tìm được nơi nào có thể sánh ngang với bò tơ chính gốc tại Củ Chi.', '');
INSERT INTO `foods` VALUES ('88', 'Rùa rang muối Cà Mau', '1', 'Ốc núi có nhiều ở các dãy núi đá vôi thuộc thị xã Tam Điệp, Yên Mô, Nho Quan. Chúng thường chỉ xuất hiện từ tháng 4 đến tháng 8 hàng năm, tức là vào mùa mưa, khi thời tiết ẩm ướt.', 'Bánh xèo có vỏ bánh vàng ruộm ở bên ngoài, bên trong có nhân tôm, thịt, giá đỗ, đúc thành hình tròn hoặc gấp lại thành hình bán nguyệt. Bánh xèo miền Nam có trứng, khi ăn chấm nước mắm chua ngọt và rất nhiều loại rau và lá cây tại miệt vườn. Tại một số nhà hàng người ta cho thêm các loại nấm làm nhân bánh xèo ăn rất ngon miệng và bớt ngán.', '');
INSERT INTO `foods` VALUES ('89', 'Cá thòi lòi nướng muối ớt Cà Mau', '1', 'Cốm là món ăn dân dã từ đồng quê nhưng ngon nức tiếng xa gần thì phải kể đến thứ cốm dẻo thơm của làng Vòng (nay thuộc phường Dịch Vọng Hậu, quận Cầu Giấy, Hà Nội). ', 'Nguyên liệu để có một cuốn gỏi cuốn chất lượng là phải đầy đủ: rau, bún, tôm, thịt ba chỉ và tất nhiên không thể thiếu bánh tráng dẻo để cuốn bên ngoài. Tuy nhiên, điều chính yếu để có thể cảm nhận một cuốn gỏi ngon là thành phần nước chấm của nó. Người Nam hay dùng gỏi cuốn chấm với tương đen vì nó có vị ngòn ngọt, cộng thêm đậu phộng và đồ chua nữa. Gỏi cuốn bình dân, thân thuộc đến thế nhưng lại lá 1 trong 50 món ăn ngon nhất thế giới do CNN bình chọn. Gỏi cuốn được bày bán rộng rãi ở khắp các hàng rong, quán cóc, chợ và cả siêu thị giá chỉ 3.000VND/cuốn.', '');
INSERT INTO `foods` VALUES ('90', 'Đuông chà là Cà Mau', '1', 'Đến phủ Tây Hồ nhớ thưởng thức bánh tôm hồ Tây', 'Quán ốc ở Sài Gòn là từ gọi chung, bởi ở đó còn có nhiều loại hải sản khác. Rủ nhau đi ăn ốc cũng là để thưởng thức một thế giới nhuyễn thể thân mềm và giáp xác phong phú, từ nghêu, sò, ốc, hến, đến cua, ghẹ, mực, tôm Có đủ cách chế biến ở Sài Gòn để món ốc luôn hấp dẫn, từ hấp, luộc, xào, chiên, rang đến nướng mọi, đút lò với đủ thứ gia vị như tỏi, hành, tiêu, ớt, rau răm, sả, gừng, húng quế, phô mai, bơ.', '');
INSERT INTO `foods` VALUES ('91', 'Vọp nướng Cà Mau', '1', 'Ở Hà Nội bây giờ có rất nhiều quán bán chả cá nhưng địa chỉ lâu đời nhất phải kể đến quán Chả cá Lã Vọng – 14 phố Chả Cá.', 'Chè miền Nam rất phong phú, từ các loại chè nóng như chè bà ba, chè chuối thưng, chè khoai môn đến chè lạnh như chè đậu đen, chè thập cẩm, chè Huế, chè của người Hoa đều rất hấp dẫn với vị nước cốt dừa béo ngậy và vị ngọt, bùi của nguyên liệu.', '');
INSERT INTO `foods` VALUES ('92', 'Lẩu mắm Dạ Lý', '1', '', '\"Khoảng tháng 7, tháng 8 Âm lịch hằng năm là vào mùa ba khía. ', '');
INSERT INTO `foods` VALUES ('93', 'Vịt nấu chao', '1', '', '', '');
INSERT INTO `foods` VALUES ('94', ' Nem nướng Thanh Vân', '1', '', 'Ba khía vùng này ăn trái đước, vẹt, đặc biệt là trái mắm đen – thứ cây có nhiều rễ bám sâu vào lòng đất của vùng bãi bồi, hàng năm rụng trái làm đen cả nước – nên gạch son nhiều và thịt chắc, ăn rất thơm.\"', '');
INSERT INTO `foods` VALUES ('95', 'Bánh xèo 7 tới', '1', 'Đi phủ Tây Hồ, đừng quên ăn bún ốc ', 'Cá khi vừa bắt dưới sông lên, rửa sạch, dùng que tre xuyên cá từ đầu đến đuôi rồi vùi cá vào đống rơm khô, châm lửa đốt hoặc cắm que xuống đất lấy rơm phủ lên và đốt lửa cho đến khi tro tàn. Khi cá chín, cạo bỏ lớp vẩy đã cháy xém để lộ ra thịt cá trắng và thơm.', '');
INSERT INTO `foods` VALUES ('96', 'Bánh cống Cô Út', '1', '\"Lẩu mắm Nam Bộ 94AB Cao Thắng, P.4, Q.3, giá: 160.000 – 240.000VND;', '\"Nguyên liệu chính của món lẩu mắm U Minh cũng là mắm sặc (nhưng phải là mắm ngon). Mắm được nấu rả thịt, lược kỹ xương, nêm nếm bột ngọt, đường cho vừa ăn rồi cho vào lẩu. Để cho dậy mùi người ta còn bổ sung thêm một ít lá sả bằm mịn, phần gốc sả đập giập cho vào lẩu.', '');
INSERT INTO `foods` VALUES ('97', 'Bánh Khọt Gốc Vú Sữa', '1', 'Lẩu mắm Ăn là khen: 11 Hồ Biểu Chánh, P.12, Q. Phú Nhuận, giá:', '', '');
INSERT INTO `foods` VALUES ('98', 'Bánh bông lan trứng muối gốc cột điện', '1', 'Lẩu mắm: 210.000 – 300.000VND.', 'Bí quyết làm cho nước lẩu có vị béo, thơm và sánh người ta cho vào một ít sữa bò thay đường. Lẩu mắm hạp với nhiều lọai thịt hay cá tùy thích. Nhưng lẩu mắm U Minh nhất định phải được nấu với cá đồng. Ngon nhất là lươn, cá rô mề, cá lóc to hoặc cá trê trắng mới trúng sách.\"', '');
INSERT INTO `foods` VALUES ('99', 'Lẩu Cá Đuối Trương Công Định', '1', '\"', '\"Nhọng ong chế biến rất nhiều món ăn ngon như cháo nhộng ong, nhộng ong xào, nhộng ong trộn gỏi.', '');
INSERT INTO `foods` VALUES ('100', 'Cơm niêu ', '1', '\"Bún mắm Huỳnh Lâm (gia truyền Bạc Liêu), 37A Gò Dầu, Q. Tân Phú: 28.000VND/tô;', '', '');
INSERT INTO `foods` VALUES ('101', 'Cháo Hàu', '1', 'Quán bún mắm 444 – 369 Lê Quang Định, P.5, Q.Bình Thạnh, mở cửa từ 9h đến 21h hàng ngày, trừ ngày rằm và mùng 1, giá 35.000VND/tô;', 'Trong đó, món nhộng ong trộn gỏi được ưa chuộng vì có thể kết hợp vị ngon bùi của nhộng ong và vị thơm của các loại rau.\"', '');
INSERT INTO `foods` VALUES ('102', 'Gỏi cá mai ', '1', 'bún mắm 528 Phan Văn Trị, Q.Gò Vấp, giá 30.000VND/tô.', '\"Chuột đồng làm sạch để ráo nước, ướp với sả ớt bằm nhuyễn, rồi thêm muối, bột ngọt, nước mắm phải thật ngon, một chút đường cho vị dịu xuống. Đợt một lúc cho gia vị ngấm vào thịt chuột rồi mới đem chiên trên lửa riu riu, nhớ đảo đều để miếng thịt chuột chín đều và vàng ruộm là ngon.', '');
INSERT INTO `foods` VALUES ('103', 'Hủ tíếu', '1', '\"', '', '');
INSERT INTO `foods` VALUES ('104', 'Bún mắm Chị Bé', '1', '\"Số 4 Nguyễn Thị Diệu, P.6, Q.3,', 'Món này ăn với cơm gạo mới nấu thơm lừng thì không thể nào dừng được. Vừa đậm tình quê vừa ngon đến tận miếng cuối cùng, vừa ăn vừa xuýt xoa vừa hít hà vừa no căng bụng vừa tràn đầy tình yêu mến đối với đất Cà Mau.\"', '');
INSERT INTO `foods` VALUES ('105', 'Bánh cuốn chả mực bà Ngân ', '1', 'Các quán lẩu cá kèo trên đường Sư Thiện Chiếu.', '\"Tôm tít vừa ngon, ngọt, hiền lành vừa đậm đà hương biển, khác xa với mùi vị của tôm sú và tôm hùm.Cách chế biến tôm tít không cầu kỳ lắm, chỉ cần hấp, luộc hoặc nướng, chấm muối tiêu chanh hoặc nước mắm me cũng đủ níu chân khách mọi miền.', '');
INSERT INTO `foods` VALUES ('106', 'Món ngán', '1', '\"', '', '');
INSERT INTO `foods` VALUES ('107', ' Bánh “gật gù”', '1', '\"Hủ tiếu mì trên đường Mạc Thị Bưởi (Q.1), hủ tiếu cá gà trên đường Tôn Thất Đạm (Q.1).', 'Thịt tôm tít đỏ hồng, mùi phưng phức, mềm mại nhưng không bở. Dân sành điệu dùng món này thường kèm thêm rau sống, cà chua, chuối chát, dưa leo và nếu như có thêm chút mù tạt càng ngon. Thịt tôm nếu đem cuốn với bánh tráng rau sống chấm nước mắm chua – cay, chưa chắc có món cuốn nào bì kịp.\"', '');
INSERT INTO `foods` VALUES ('108', 'Bún và bánh đa hải sản', '1', 'Hủ tiếu mì vàng trên đường Hải Thượng Lãn Ông (Q.5).', 'Rùa có thể làm được rất nhiều món ăn ngon và bổ, nhưng ngon nhất vẫn là món rùa rang muối, vừa dễ làm, dễ tìm gia vị,thơm giòn, ngon ngọt, béo ngậy của thịt rùa, vị cay cay của rau răm, rau húng, vị cay nồng, ngòn ngọt thực sự là một món ăn dân dã đặc biệt của người xứ Cà Mau.', '');
INSERT INTO `foods` VALUES ('109', 'Sam ', '1', 'Hủ tiếu 46/102 Võ Văn Tần, Q.3, mì Vịt tiềm trên đường Phạm Văn Hai (Q.Tân Bình), mì chú Tắc: 22 Bà Huyện Thanh Quan, Q.3.\"', 'Thòi lòi là một loại sinh vật lạ đời, là cá nhưng lại nhảy loi choi, kiếm mồi trên mặt nước, đất bùn và sống trong hang.', '');
INSERT INTO `foods` VALUES ('110', 'Ốc xào', '1', '\"Bạn có thể ăn bánh tráng Trảng Bàng ở hệ thống cửa hàng của Hoàng Ty.', 'Không những độc đáo và ngon miệng, đuông chà là còn được ưa thích bởi vì nó giàu chất dinh dưỡng bổ béo. Đuông chà là hiện nay rất đắt và quý.', '');
INSERT INTO `foods` VALUES ('111', 'Bún quậy Kiến Xây ', '1', 'bánh tráng Trảng Bàng 411 – 413 Nguyễn Tri Phương, Q.10.', 'Vọp là một hải sản có hình dạng giống nghêu là món ăn đặc sản của Cà Mau . Vọp được chế biến thành nhiều món: vọp nấu chua cơm mẻ, vọp hấp gừng, vọp kho tộ, gỏi vọp bắp chuối rau thơm, nhưng đứng đầu vẫn là vọp nướng mỡ hành.', '');
INSERT INTO `foods` VALUES ('112', 'Bánh canh chả cá Phụng ', '1', '\"', '\"Để được mùi vị đặc biệt của nem nướng Thanh Vân, chủ quán phải chọn thịt heo thật tươi mới, được quết thật nhuyễn bằng cối đá, sau đó thịt được quết sẽ được ướp gia vị theo công thức truyền thống của quán, rồi được vo thành viên, xỏ que nướng trên than hồng.', '');
INSERT INTO `foods` VALUES ('113', 'Cơm tấm Huỳnh Nhu Nguyễn Trãi', '1', '\"Quán bò tơ Xuân Đào – Đi thẳng trên đường Quốc lộ 22, Qua ngã tư Trung chánh và đến cầu vượt Củ Chi, nhìn phía tay phải sẽ thấy quán bò tơ Xuân Đào, huyện Củ Chi;', 'Món nem nướng Thanh Vân còn đặc biệt ở chỗ có thêm món tằm bì cắt nhuyễn cùng chả giò chay với nhân bên trong được làm bằng khoai môn ngọt tạo nên mùi vị hấp dẫn tuyệt vời, khó lòng quên được.\"', '');
INSERT INTO `foods` VALUES ('114', 'Ốc hương nướng muối ớt', '1', ' Bò tơ Hồng Đào cũng ở Quốc lộ 22 nhưng bên tay trái.\"', '\"Nguyên liệu chính để nấu món này là thịt vịt (vịt ta hay vịt xiêm, miền Bắc gọi là ngan). Chọn một con từ 1,5 kg trở lên, vịt làm xong, thoa ngoài da một lớp nước gừng và rượu. Chặt miếng vừa phải, ướp gia vị: chao trắng, bột ngọt, tỏi, tiêu, ớt, nước cốt dừa vừa đủ, để khoảng 30 phút đem chiên sơ qua trước khi nấu.', '');
INSERT INTO `foods` VALUES ('115', 'Bún kèn', '1', '\"46A Đinh công Tráng, Q.1; Mười Xiềm, 190 Nam Kỳ Khởi Nghĩa, P.6, Q.3;', ' ', '');
INSERT INTO `foods` VALUES ('116', 'Ghẹ hấp Hàm Ninh', '1', 'Ăn Là Ghiền, 74 Sương Nguyệt Ánh, P.Bến Thành, Q.1.', 'Khoai môn (khoai sọ) luộc vừa chín, bóc vỏ, chiên qua. Khi thịt vịt vừa chín tới thì cho khoai, hành tây, nấm rơm búp vào để sôi khoảng 15 phút rồi nhấc xuống.', '');
INSERT INTO `foods` VALUES ('117', 'Bánh tét Mật Cật\n Phú Quốc', '1', '\"', ' ', '');
INSERT INTO `foods` VALUES ('118', 'Bún cá', '1', '\"Vỉa hè công viên Lê Văn Tám (đường Hai Bà Trưng),', 'Khi ăn cho hỗn hợp vào lẩu (dùng than đước, hoặc bếp ga nhỏ) để lửa liu riu ăn kèm các loại rau cải như cải cúc, rau muống, tàu hũ, bún tàu hay mì sợi. Có người thích ăn thêm trứng vịt lộn hay rau diếp cá, rau cần, cù nèo, giá... Vịt nấu chao phải ăn nóng mới ngon.', '');
