INSERT INTO `foods` VALUES ('140', 'Bánh canh', '1', '\"tọa lạc trên đường ', ' rút xương ướp gia vị để làm chín cá. Những gia vị ướp cá tươi để làm gỏi không phức tạp, mà đơn giản như những món gỏi cá khác như giấm, tỏi, chanh và ớt. Cá đã tái, sẽ được trộn thêm thính, đậu phụng giả giập để tăng mùi thơm và cá sẽ khô hơn, rắc thêm một ít đậu phụng lên mặt để trang trí.', '');
