INSERT INTO `gifts` VALUES ('2', 'Kẹo dừa', 'Kẹo dừa Bến Tre vừa thơm, vừa béo là món quà ý nghĩa để tặng người thân khi đi du lịch Bến Tre', '0903.964.730', 'http://www.keoduathanhlongvn.com.vn/', null, '1');
INSERT INTO `gifts` VALUES ('3', 'Rượu dừa', 'Rượu được làm từ chính quả dừa - đặc sản của vùng Bến Tre nên khi uống bạn sẽ cảm nhận được mùi vị thơm mát của dừa hòa quyện cùng rượu nếp.', '093 235 93 40', '', null, '1');
INSERT INTO `gifts` VALUES ('4', 'Quà lưu niệm từ dừa', 'Sản phẩm thủ công mỹ nghệ dừa được làm từ dừa rất kỳ công. Sản phẩm dùng để làm quà lưu niệm hoặc trang trí rất đẹp và có ý nghĩa.', '0967 789998', 'http://idacsan.vn/', null, '1');
INSERT INTO `gifts` VALUES ('5', 'Bánh tráng Mỹ Lồng', 'Bánh tráng Mỹ Lồng thơm giòn, khi nướng chính có màu rất đẹp và ăn béo béo vị dừa, là món quà không thể thiếu khi du lịch Bến Tre.', '0968 789998', 'http://idacsan.vn/', null, '1');
INSERT INTO `gifts` VALUES ('6', 'Bánh phồng Sơn Đốc', 'Bánh phồng Sơn Đốc ăn vừa giòn vừa thơm là món đặc sản không thể không mua về.', '0969 789998', 'http://idacsan.vn/', null, '1');
INSERT INTO `gifts` VALUES ('7', 'Xà phòng dừa', 'Xà phòng dừa handmade kết hợp với mỗi loại tinh dầu sẽ tạo ra công dụng rất đặc trưng, không thể không mua về làm quà.', '08 22209279', 'http://coco-secret.vn/', null, '1');
INSERT INTO `gifts` VALUES ('8', 'Mứt dừa', 'Mứt dừa tuy dân dã mộc mac nhưng đã làm biết bao nhiêu người nhớ mãi, từ hương thơm nhè nhẹ, beo béo tự nhiên của dừa kết hợp với vị ngọt ngọt, thanh thanh của đường đã làm bạn ăn hoài không chán.', '093 925 83 05', 'https://www.facebook.com/mutduahomemade/', null, '1');
INSERT INTO `gifts` VALUES ('9', 'Bánh tráng chuối', 'Bánh tráng chuối ngon nhờ sự hòa quyện của vị béo nước cốt dừa, vị bùi của khoai mỳ và nếp và nhất là mùi vị thơm ngọt tự nhiên của chuối.', '0969 789998', 'http://idacsan.vn/', null, '1');
INSERT INTO `gifts` VALUES ('10', 'Rượu Phú Lễ', 'Mang hương vị vùng châu thổ sông Mekong, đậm đà, phóng khoáng như năng gió Phương Nam, như cái mênh mang của vùng sông nước Nam Bộ, rượu Phú Lễ từ lâu đã được đưa vào hàng đệ nhất danh tửu.', '0753 763116', 'http://ruouphule.com/', null, '1');
INSERT INTO `gifts` VALUES ('11', 'Bánh tráng phơi sương Trảng Bàng', 'Bánh tráng phơi sương đơn giản, mộc mạc không cầu kỳ như các loại bánh tráng khác nhưng lại xuất hiện trong mọi bữa ăn gia đình, là món quà mà bất cứ ai cũng muốn mua về cho người thân, bạn bè.', '0977 669411', 'http://banhtrangme.com/', null, '1');
INSERT INTO `gifts` VALUES ('12', 'Muối tôm Tây Ninh', 'Không như bánh canh hay bánh tráng Tây Ninh, muối tôm là một đặc sản nổi tiếng có phần nghịch lý của Tây Ninh. Ở vùng đất thiếu cả muối lẫn tôm mà lại nổi tiếng với đặc sản là muối tôm. Tuy nhiên, đó là bí quyết, niềm tự hào của người dân Tây Ninh.', '0978 669411', 'http://banhtrangme.com/', null, '1');
INSERT INTO `gifts` VALUES ('13', 'Nem Bưởi', 'Nem Bưởi là đặc sản Tây Ninh\ncó vị hơi chua, được làm từ vỏ bưởi mang đến vị chua chua, cay cay, một chút mặn mặn và ngọt nhẹ.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('14', 'Nấm hương rừng Sapa', 'Về hình dáng, nấm hương rừng Sapa không khác là mấy so với các loại nấm hương khác. Nhưng hương vị thì không có bất kỳ loại nấm hương thông thường nào có thể sánh bằng.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('15', 'Đào rọ Sapa và mận hậu Bắc Hà', 'Nếu bạn đến Sapa vào mùa hè, bạn sẽ được đắm chìm trong vẻ đẹp đến nao lòng của những vườn đào, mận chín mọng, sai trĩu và nhất là được thưởng thức những quả đào rọ bằng cái chén mặt châu, thơm lừng, có vị ngọt mà hơi chua chát; những quả mận hậu ngoài tí', '', '', null, '1');
INSERT INTO `gifts` VALUES ('16', 'Măng vầu Sapa', 'Vị ngọt thanh, hơi đắng của măng vầu Sapa đã chiếm trọn “cái dạ dày” của khách du lịch khi đến đây.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('17', 'Rau cải mèo Sapa', 'Đây là giống cải chỉ có ở Sapa. Nếu bạn đã từng được nếm vị giòn dai, đắng đầu lưỡi ngọt cuống họng thì bạn sẽ không bao giờ có thể quên được những món ăn được chế biến từ loại cải này. ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('18', 'Rượu táo mèo và rượu San Lùng', 'Là một loại rượu đặc sản của người dân tộc Dao đỏ, được sản xuất tại Thôn San Lùng - xã Bản Xèo - huyện Bát Xát - tỉnh Lào Cai.', '0203 626668', 'http://ruousanlung.com.vn/', null, '1');
INSERT INTO `gifts` VALUES ('19', 'Thịt trâu gác bếp', 'Nếu đã nói đến những món đặc sản Sapa nên mua về làm quà thì không thể bỏ qua thịt trâu gác bếp. Khác với những nơi khác, thịt trâu gác bếp của người Sapa được làm hoàn toàn bằng trâu bản, đem tẩm ướp những loại gia vị bí truyền và sấy khô bằng sức nóng v', '', '', null, '1');
INSERT INTO `gifts` VALUES ('20', 'Tương ớt Mường Khương', 'Không giống với những loại tương ớt thông thường, tương ớt Mường Khương được chế biến từ loại ớt thóc đặc biệt, kết hợp với tỏi củ, hạt rau thì là, hạt rau mùi, thảo quả, hạt dồi, quế, muối, rượu và nước, theo một tỷ lệ gia truyền.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('21', 'Thanh Long Phan Thiết', 'Thanh long Phan Thiết không chỉ được xuất bán cho các tỉnh thành trên cả nước mà còn được nhập sang các quốc gia khác trong khu vực.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('22', 'Nước mắm Phan Thiết', 'Được chế biến từ những con cá cơm tươi rói cùng sự cần mẫn của con người miền biển và bí quyết gia truyền riêng đã cho ra những giọt nước mắm Phan Thiết ngon tuyệt.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('23', 'Bánh rế Phan Thiết', 'Bánh rế là một trong những đặc sản nổi tiếng vùng đất Phan Thiết. Bánh được làm từ khoai lang hoặc khoai mì bào sợi, sau đó tạo thành hình tròn và chiên trên chảo dầu sôi sục.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('24', 'Mực một nắng Phan Thiết', 'Mực một nắng Phan Thiết là món đặc sản nức tiếng tại thành phố biển xinh đẹp này.Mực một nắng chính là những chú mực lá sau khi được ngư dân đánh bắt về đem phơi dưới ánh mặt trời đúng 1 ngày rồi đóng gói và ướp lạnh thật kỹ.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('25', 'Hải sản khô Phan Thiết', 'Khi đi du lịch Phan Thiết, bạn sẽ dễ dàng lựa chọn mua các loại hải sản khô về làm quà tặng người thân và bạn bè như khô cá chỉ vàng, khô mực, tôm khô, cá rim, mực rim chua ngọt, …', '09754 08 953', 'http://haisansachphanthiet.com/hai-san/hai-san-kho/', null, '1');
INSERT INTO `gifts` VALUES ('26', 'Mè xững', 'Là niềm tự hào của người dân Huế, mè xửng là thứ kẹo ngon lại còn dai dai, ăn hoài vẫn không thấy ngán. Mè (vừng) và xững (cách hoán đường) hợp thành tên kẹo.', '054. 3511246', 'http://www.mexungthienhuong.com.vn/index.php/thienhuong', null, '1');
INSERT INTO `gifts` VALUES ('27', 'Nem và Tré', 'Đây cũng là hai món ăn độc đáo ở Huế, được nhiều du khách lựa chọn mua về làm quà khi du lịch Huế cho người thân khi đến Huế. Nem Huế có cách nêm gia vị đặc trưng riêng mà nem miền Bắc và miền Nam không có.', '098 352 75 46', 'http://dongbahue.com/', null, '1');
INSERT INTO `gifts` VALUES ('28', 'Hạt sen', 'Sen thì nơi đâu mà không có, từ Bắc đến Nam chỗ nào cũng có sen, có sen tức là có hạt sen nhưng nếu nói hạt sen ngon, dở, đậm đà thì không đâu bằng sen Huế nhất là sen hồ Tịnh.', '054.35.35.978', 'https://www.dacsanhue.com.vn/?cat_id=1#', null, '1');
INSERT INTO `gifts` VALUES ('29', 'Dầu tràm Tiến Triều', 'Dầu tràm này có tác dụng phòng ho, tránh gió, cảm lạnh,… cho tất cả mọi người rất hiệu quả. Nó là sản phẩm không thể thiếu ở mỗi gia đình Huế.', '0122 9423014', 'http://tientrieu.vn/', null, '1');
INSERT INTO `gifts` VALUES ('30', 'Thanh trà Huế', 'Thanh trà Thủy Biều là giống cây đặc sản nổi tiếng từ hàng trăm năm nay ở Huế. Chẳng biết cây này vốn có xuất xứ từ đâu và được trồng ở Thủy Biều tự bao giờ, nhưng nghe đâu cũng đã ngót mấy trăm năm rồi.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('31', 'Mắm sò Lăng Cô', 'Cùng với tôm chua, ruốc, mắm sò Lăng Cô là món ăn đặc sản vùng đất cố đô. Lăng Cô là một thắng cảnh nổi tiếng thuộc huyện Phú Lộc, Thừa Thiên – Huế nằm ở chân đèo Hải Vân, cách thành phố Huế chừng 80 km.', '', 'http://www.hangtruyenthong.com/', null, '1');
INSERT INTO `gifts` VALUES ('32', 'Kẹo cau, kẹo gừng', 'Hai loại kẹo này đều được khách du lịch lựa chọn mua về làm quà khi du lịch Huế.', '(0234) 3 517 212', '', null, '1');
INSERT INTO `gifts` VALUES ('33', 'Mắm Huế', 'Mắm là loại gia vị không thể thiếu trong công việc nội trợ. Huế nổi tiếng với nhiều loại như mắm ruốc, mắm cá cơm, mắm rò, tôm chua,… Nổi tiếng hơn cả là mắm Lăng Cô, ngon đúng chất, không có pha chế, ăn vào thấy thật ngon và có mùi thơm nức,…', '', '', null, '1');
INSERT INTO `gifts` VALUES ('34', 'Nón bài thơ', 'Nói đến Huế, chắc ai cũng từng liên tưởng đến vẻ đẹp dịu dàng của người con gái Huế với tà áo dài tím, đứng bên bờ sông Hương trên tay cầm chiếc nón bài thơ đầy duyên dáng. Đã từ lâu, hình ảnh này đã tạo nên một dấu ấn không thể quên, một nét đặc trưng bi', '', '', null, '1');
INSERT INTO `gifts` VALUES ('35', 'Hải sản', 'Đà Nẵng – một thành phố biển nên thơ, xinh đẹp thuộc vùng duyên hải Miền Trung được thiên nhiên ưu ái, ban tặng cho những loài hải sản ngon vô cùng phong phú, đa dạng cho du khách đến thưởng thức, trải nghiệm. Cũng chính sự phong phú, đa dạng của hải sản ', '5113937368', 'nul', null, '1');
INSERT INTO `gifts` VALUES ('36', 'Rượu cần Nho Quan', 'Trong ẩm thực của Ninh Bình, nổi tiếng có 1 trong 3 loại rượu như Kim Sơn, Lai Thành hoặc rượu cần Nho Quan, thường được kèm theo các món ngon của địa phương. Nếu bạn có dịp thưởng thức rượu Kim Sơn, Lai Thành thì không nên bỏ qua Rượu cần Nho Quan. Từ cá', '901148123', 'https://chinhgoc.vn/ruou-can-nho-quan', null, '1');
