INSERT INTO `gifts` VALUES ('38', 'Nem Yên Lê', 'Trong ẩm thực của Ninh Bình, nổi tiếng có 1 trong 3 loại rượu như Kim Sơn, Lai Thành hoặc rượu cần Nho Quan, thường được kèm theo các món ngon của địa phương. Nếu bạn có dịp thưởng thức rượu Kim Sơn, Lai Thành thì không nên bỏ qua Rượu cần Nho Quan. Từ cá', '901148123', 'https://chinhgoc.vn/ruou-can-nho-quan', null, '1');
INSERT INTO `gifts` VALUES ('39', 'Đồ thủ công mỹ nghệ làm từ giấy cói ', 'Kim Sơn -Ninh Bình là vùng trồng cói, làm đồ cói lâu đời và có tiếng ở miền Bắc. Sản phẩm cói mỹ nghệ của Kim Sơn đã có mặt ở hơn 20 quốc gia và vùng lãnh thổ trên thế giới.', 'null', 'http://vinahandicrafts.com/san-pham-coi-my-nghe-kim-son-ninh-binh/', null, '1');
INSERT INTO `gifts` VALUES ('40', 'Ô mai đặc sản nhất định phải mua về làm quà khi đến Hà Nội ', 'Có lẽ không cần giới thiệu nhiều về ô mai Hà Nội bởi tiếng tăm của nó đã nức tiếng xa gần. Đây là sản vật dường như được nhiều người đặt mua nhất khi tới thăm thủ đô bởi sự lựa chọn rất phong phú, hương vị ngon, giá cả vừa phải. Ô mai được sấy khô nên cũn', '1800 6657', 'http://honglam.vn/', null, '1');
INSERT INTO `gifts` VALUES ('41', 'Quần áo vintage ', 'Đồ vintage thuần, hay còn gọi là đồ hàng thùng được bán rộng rãi ở khắp Hà Nội đã trở thành khái niệm hết sức quen thuộc, nhất là đối với những bạn sinh viên hay những người có mong muốn tìm được món đồ độc và lạ. Phong cách vintage mang đến hình ảnh nhữn', 'null', 'http://toplist.vn/top-list/cua-hang-do-vintage-chat-nhat-o-ha-noi-4535.htm', null, '1');
INSERT INTO `gifts` VALUES ('42', 'Trà sen', 'Trà ướp sen Tây Hồ còn có các tên gọi là trà sen Hà Nội, chè ướp sen Hồ Tây là sản phẩm trà độc đáo bậc nhất của người Thăng Long- Hà Nội.\n\nMột sáng tháng 6, con đường ven Hồ Tây nồng nàn hương sen dẫn chúng tôi đến với Quảng An. Chúng tôi tìm đến bác Xiê', '934568912', 'http://cheviet.vn/danh-muc/tra-uop-sen-tay-ho/', null, '1');
INSERT INTO `gifts` VALUES ('43', 'Bánh cốm', 'Bánh cốm Hà Nội từ rất nhiều năm nay, cùng với cốm làng Vòng được coi là đặc sản, là tinh túy của người Hà Nội. Nếu nói về cốm làng Vòng, như cái tên, chỉ có thể mua tại chính làng Vòng, do người dân làng Vòng sản xuất mới thật sự là ngon và đúng với cái ', '1234502353', 'http://comvonghanoi.net/116-dia-chi-ban-banh-com-ha-noi-ngon-va-re-nhat.html', null, '1');
INSERT INTO `gifts` VALUES ('44', 'Đồ gốm thủ công ', 'Những người dân Hà Nội không còn hứng thú nhiều với làng gốm Bát Tràng do đã quá quen nhưng khách phương xa thì vẫn rất yêu thích địa điểm này. Tới đây, bạn không chỉ được mua những đồ lưu niệm được làm từ gốm với mức giá rất rẻ, từ vài chục nghìn mà còn ', 'null', 'http://ngoisao.net/tin-tuc/an-choi/choi-dau/nhung-mon-qua-mang-ve-khi-du-lich-ha-noi-3105404.html', null, '1');
INSERT INTO `gifts` VALUES ('45', 'Bánh tét lá cẩm', 'Bánh tét lá cẩm có mùi vị đặc biệt không lẫn vào đâu được . Mùi thơm của lá cẩm, béo ngậy của thịt và nước cốt dừa hòa quyện với nhân đậu xanh ngon không thể cưỡng lại được. Nếp phải là loại nếp rặt không lộn gạo, thịt làm nhân được lựa chọn kỹ lưỡng và t', '908458316', '', null, '1');
INSERT INTO `gifts` VALUES ('46', 'Ca cao Phong Điền', 'Để có được bột ca cao thơm ngon mà chúng ta thường dùng làm nước uống thì người trồng ca cao phải trải qua nhiều công đoạn.', ' 07103 942 573', '', null, '1');
INSERT INTO `gifts` VALUES ('47', 'Nem nướng Thanh Vân', 'MÓn nem rất ngon và đẹp mắt. Quán sạch sẽ nhưng rất đông, chờ món cũng mất tầm 10\' nhưng được cái có nhiều rau, nhiều bánh tráng để cuốn, nước mắm vừa ăn. Nhân viên khá ổn.', '84 710 3827 255', '', null, '1');
INSERT INTO `gifts` VALUES ('48', 'Rượu Mận Sáu Tia Cần Thơ', 'Rượu Mận Cần Thơ Sáu Tía là sản phẩm rượu đặc sản nổi riếng khắp đồng bằng sông cửu long và được rất nhiều khách hàng yêu thích bởi mang hương vị tự nhiên của mận và quan trọng nhất đó chính là rất tốt cho sức khoẻ\nĐặc trưng của rượu mận sáu tia là không ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('49', 'Bánh tráng Thuận Hưng', 'Bánh tráng ở đây được làm quanh năm, nhưng mùa cao điểm nhất là vào mùa tết, nhu cầu tăng cao. Bánh tráng ở đây được các tỉnh lân cận tìm đến mua và xuất khẩu cả sang Campuchia. Bánh tráng Thuận Hưng được ưa chuộng bởi chiếc bánh mịn đều, dẻo thơm, đặc bi', '', '', null, '1');
INSERT INTO `gifts` VALUES ('50', 'Hoa quả, trái cây', 'Là xứ trái cây nhưng nhìn chung giá trái cây được bày bán ở chợ cũng không phải là quá rẻ so với thành phố và những vùng khác.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('51', 'Bánh bông lan trứng \nmuối', '1 hộp bông lan trứng muối chà bông 30k, hộp 10 cái, trung bình 3k/cái, khá nhỏ, hơi đắt nhưng mà bột cực thơm, ngon ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('52', 'Mắm Trí Hải', 'Măm Trí Hải – Chia sẻ bữa ăn cùng gia đình bạn” là thương hiệu và khẩu hiệu đã được đăng ký độc quyền và đã đi vào tiềm thức của người dân Nam bộ nói riêng và cả nước nói chung. Nhắc đến Bà Rịa - Vũng Tàu là nhắc đến “Mắm Trí Hải”. Có thể nói, Mắm Trí Hải', '064. 716088  064. 57', '', null, '1');
INSERT INTO `gifts` VALUES ('53', 'Đồ thủ công mỹ nghệ', 'Tại đây, du khách có thể tìm thấy các mặt hàng được làm từ vỏ sò, ốc (tạo hình, cẩn ghép tranh, phù điêu, đồ gia dụng…), hàng sơn mài, đá non nước… Trong mỗi món hàng đều mang hơi thở riêng của biển. Để lắng nghe tiếng sóng biển rì rào, du khách nên chọn ', '064 859104', '', null, '1');
INSERT INTO `gifts` VALUES ('54', 'Tơ lụa Thổ cẩm', 'Nếu du khách muốn có những món quà mang chút vẻ hoang dã của tây nguyên, hay mang đậm hồn Việt ở thành phố biển Vũng Tàu, du khách có thể tìm thấy tại Shop thời trang Gia Đình. Từ chất liệu truyền thống tự nhiên của người Việt là tơ tằm, thổ cẩm, những sả', ' 064 511536', '', null, '1');
INSERT INTO `gifts` VALUES ('55', 'Mực một nắng', 'Mực một nắng là một món ăn hải sản Việt Nam và là đặc sản của vùng biển Vũng Tàu.Mực được phơi qua 01 nắng cho mực săn lại, bên ngoài mực khô nước nhưng bên trong vẫn còn rất tươi ngon, dẻo và có màu trắng đục. Mực một nắng vẫn giữ được vị ngọt, thơm tự n', '', '', null, '1');
INSERT INTO `gifts` VALUES ('56', 'Hải sản khô', 'Nếu mua hải sản tươi thì việc mang về sau một chặng đường dài dễ khiến hải sản bị chết và không còn ngon như lúc mới mua. Vậy nên sự lựa chọn tốt nhất để làm quà mang về khi đi du lịch Vũng Tàu chính là đồ hải sản khô bởi loại hải sản này để được lâu và ă', '', '', null, '1');
INSERT INTO `gifts` VALUES ('57', 'Hải sản tươi sống', 'Hải sản tươi luôn hấp dẫn du khách bởi độ tươi của nó khi được bắt từ biển về, vì vậy đây là quà mà du khách không thể bỏ lỡ mỗi khi du lịch biển.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('58', 'Mực khô', 'Mực khô ngon thường có thân thẳng, mình dày, màu hồng sáng, khi sờ vào không thấy ướt tay. Đầu mực khô, có râu; đầu gắn liền chắc vào thân mực; đuôi mực mỏng, ôm sát vào thân.\nMực khô kém chất lượng thường không có màu hồng sáng, hình thù gần giống lá trầ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('59', 'Sái sùng khô', 'Giá của loại Sái Sùng khô này gấp 6-7 lần giá mực khô, khoảng từ 4,0 – 5triệu/kg\nSái Sùng khô kém chất lượng là loại đã chuyển màu xanh nhạt có lấm tấm trắng, nhỏ, vụn vì để lâu ngày. Còn nếu loại sá sùng mỏng, màu trắng trong, ngửi không có mùi thơm đặc ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('60', 'Chả mực', 'Nếu muốn mua đặc sản Chả mực giã tay Hạ Long, quý khách nên mua nếm thử trước. Với loại chả mực ngon sẽ có mùi thơm nức, dòn và dai. Loại chả mực không ngon thường có mùi hơi ngái, bở, bột, không dai và có cả chút nước ở trong.\nQuý khách nên chọn mua của ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('61', 'Đồ lưu niệm', 'Loại đồ lưu niệm có giá tương đối là những loại chất liệu tù gỗ, khảm trai, ngọc trai, vỏ sò, ốc,... ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('62', 'Ngọc trai Phú Quốc', 'Với đường bờ biển dài và sạch, Phú Quốc là nơi có môi trường tốt nhất để loài trai sinh sống và phát triển. Tận dụng được điều này, người dân Phú Quốc từ lâu đã thử nghiệm mô hình nuôi cấy ngọc trai bán nhân tạo và đạt được những thành công rực rỡ. Đến na', '0773 988 999', 'ngoctraingochien@gmail.com', null, '1');
INSERT INTO `gifts` VALUES ('63', 'Nước mắm Phú Quốc', 'Nước mắm Phú Quốc nổi tiếng với hương vị thơm ngon, đậm đà, là món ăn thường xuất hiện trong các bữa ăn gia đình Việt. Nước mắm Phú Quốc có màu cánh gián đậm, trong và mùi thơm nhẹ rất riêng; vị mặn, vị ngọt có cả vị béo. Nước mắm Phú Quốc ngon nhờ được c', '077 3848555 ', 'www.khaihoanphuquoc.com.vn', null, '1');
INSERT INTO `gifts` VALUES ('64', 'Rượu Sim Phú Quốc', 'Trái sim dùng làm rượu ở Phú Quốc chủ yếu là hồng sim, được sản xuất truyền thống lên men tự nhiên từ trái sim rừng và đường cát trắng, có thể pha thêm rượu đã làm để tăng nồng độ. Rượu vang sim Phú Quốc được coi là một loại dược tửu, được chế biến từ trá', '', '', null, '1');
INSERT INTO `gifts` VALUES ('65', 'Hồ tiêu', 'Hồ tiêu là một trong những đặc sản Phú Quốc mà nhiều du khách hay mua về làm quà cho bạn bè, người thân. Tiêu Phú Quốc sau khi thu hoạch thường được người dân chia làm 3 loại tiêu sọ, tiêu đỏ, tiêu đen. Tiêu sọ nguyên hạt, tiêu sọ xay, tiêu chính nguyên h', '', '', null, '1');
INSERT INTO `gifts` VALUES ('66', 'Bánh khéo', 'Tại Phú Quốc, bánh khéo rất được người dân trên đảo và khách du lịch tin dùng. Bánh khéo được chế biến từ bột mì với nhiều hương vị của nhân bánh như dừa, đậu xanh, khoai môn … có độ ngọt thanh, ít béo, không gây cảm giác ngán.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('67', 'Bánh tét Mật Cật\n Phú Quốc', 'Là một vùng biển đặc trưng, dĩ nhiên Phú Quốc không thể thiếu những sản phẩm khô được chế biến từ hải sản. Các loại hải sản khô được bán rất nhiều ở chợ Dương Đông – Phú Quốc. Hải sản khô Phú Quốc rất phong phú, chỉ cần dạo một vòng chợ Dương Đông, du khá', '', '', null, '1');
