INSERT INTO `gifts` VALUES ('69', 'Tôm khô ', 'Nếu nhất định phải mua gì đó ở đảo Nam Du thì tốt nhất là mua khô cá, thực phẩm này có thể hàng năm mà không hư, di chuyển dễ dàng, tiện lợi. Những con cá sau khi được đánh bắt về, được người dân sơ chế sạch, sau đó đem đi phơi khô, cá đảm bảo được vị tươ', '', '', null, '1');
INSERT INTO `gifts` VALUES ('70', 'Đồi mồi, Huyền Hà Tiên', 'Các bạn sẽ dễ dàng bắt gặp hình ảnh những anh chị chở những gói tôm, mực, cá khô ở các bến tàu và trước những khách sạn lớn hoặc nếu có tham quan trong chợ Bách Hóa Hà Tiên cũng có những sạp bán mặt hàng này. Mặt hàng này rất nhạy cảm về giá nên các bạn c', '', '', null, '1');
INSERT INTO `gifts` VALUES ('71', ' Áo thun, nón, hàng thủ \ncông mỹ nghệ', 'Nghề đồi mồi thuở xưa đã làm giàu khá nhiều gia đình và làm nên danh tiếng Hà Tiên- vùng đất biên thùy với biển đảo bao la. Đây là môi trường lý tưởng để loài sinh vật đẹp đẽ này sinh cư, phát triển và được khai thác chế biến thành những vật trang sức, tr', '', '', null, '1');
INSERT INTO `gifts` VALUES ('72', 'Các mặt hàng có xuất xứ từ Thái Lan', 'Cũng giống như những điểm du lịch khác ở Việt Nam, bạn có thể mua cho mình hoặc bạn bè, người thân một chiếc áo hoặc nón in chữ Du lịch Hà Tiên và một số mặt hàng thủ công mỹ nghệ rất đẹp và bắt mắt.', '', '', null, '1');
INSERT INTO `gifts` VALUES ('73', 'Rượu vang Đà Lạt', 'Có thể bạn sẽ dễ dàng tìm thấy những mặt hàng có xuất xứ từ Thái Lan ở các sạp chợ khác nhưng để mua được đúng các mặt hàng có chất lượng chính gốc từ Thái Lan thì bạn nên mua ở Hà Tiên. Các mặt hàng bao gồm: Dầu cù là Thái Lan, Dầu gió Thái Lan, Dầu nóng', '', '', null, '1');
