INSERT INTO `hotels` VALUES ('20', 'Khách sạn Thảo My', '313 Trần Hưng Đạo, phường 1, thị xã Tây Ninh', '066 3829 475', 'Khách sạn Thảo My là nơi dừng chân lý tưởng của nhiều du khách khi dừng chân tại thị xã Tây Ninh đầy năng động.', '', '250000', null, null, '2', '1', '1', '11.3255463', '106.0994415', '2017-09-22 23:19:12', null, 'hotel1.jpg', null);
INSERT INTO `hotels` VALUES ('21', 'Khách sạn Amazing Sa Pa', 'đường Đồng Lợi – thị trấn Sa Pa – huyện Sa Pa', '0214 3865 888', 'Khách sạn Amazing thu hút du khách bởi vẻ đẹp sang trọng trong lối kiến trúc hiện đại độc đáo. Từ các phòng nghỉ của khách sạn, du khách sẽ được chiêm ngưỡng phong cảnh thiên nhiên kỳ vỹ của dãy núi Hoàng Liên, đỉnh Fansipan, hướng tầm nhìn theo thung lũng Violet và thung lũng Mường Hoa với những ngôi nhà xinh xắn được xen lẫn trên những thửa ruộng bậc thang.', '', '1098701', null, '4', '3', '1', '1', '22.3327599', '103.8401947', '2017-09-22 23:20:06', null, 'ks.jpg', null);
INSERT INTO `hotels` VALUES ('22', 'Khách sạn Bamboo', 'số 18- đường Mường Hoa – thị trấn Sa Pa', '+84 2143 871 075', 'Nằm nép mình dưới chân núi Hàm Rồng ở trung tâm thị trấn với tầm nhìn ra dãy núi Hoàng Liên hùng vỹ và thung lũng Mường Hoa thơ mộng, du khách sẽ được chiêm ngưỡng sự hung vỹ của núi rừng ngay từ khung cửa sổ của 56 phòng khách của khách sạn Bamboo Sa Pa.', 'http://www.bamboosapahotel.com.vn/vi/', '966000', '2017-10-14', '3', '3', '1', '1', '22.3323727', '103.8415375', '2017-10-15 00:57:25', null, 'ks7.jpg', null);
INSERT INTO `hotels` VALUES ('23', 'Khách sạn Cat Cat View', '46, Phanxipan, SaPa, Lào Cai', '0214 3871 601', 'Với lối kiến trúc mang đậm phong cách văn hóa địa phương, dựa lưng vào núi và quay mặt về phía dãy núi Hoàng Liên và thung lũng Mường Hoa.', 'http://www.catcathotel.com/', '863914', null, '3', '3', '1', '1', '22.3333054', '103.8382797', '2017-09-22 23:21:59', null, 'ks3.jpg', null);
INSERT INTO `hotels` VALUES ('24', 'Khách sạn Châu Long Sa Pa', 'thị trấn SaPa', '(+84) 214 3871 245', 'Tọa lạc trên con phố yên tĩnh ngay tại trung tâm thị trấn Sa Pa, với 65 phòng khách đều có hướng nhìn ra dãy núi Fansipan và thung lũng Mường Hoa.', 'http://chaulonghotel.com/', '1455013', null, '4', '3', '1', '1', '22.3327789', '103.8400726', '2017-09-30 21:22:11', '1', 'ks3.jpg', null);
INSERT INTO `hotels` VALUES ('25', 'Khách sạn Hmông Sa Pa', '27 thác Bạc, thị trấn SaPa', '0214 3772 228', 'Nằm trên một con phố yên tĩnh về phía tây bắc của thị trấn Sa Pa, với sự kết hợp tinh tế giữa lối kiến trúc hiện đại đan xen với bản sắc văn hóa của cộng đồng người Hmông nơi đây', 'http://www.hmongsapahotel.com/vi/', '920750', null, '3', '3', '1', '1', '22.3348179', '103.8356018', '2017-09-22 23:22:46', null, 'ks4.jpg', null);
INSERT INTO `hotels` VALUES ('26', 'Khách sạn Holiday Sa Pa', 'phố Cầu Mây – thị trấn Sa Pa', '0214 3873 874', 'Khách sạn Holiday Sa Pa nằm dựa vào núi và hướng về  phía dãy núi Hoàng Liên và thung lũng Mường Hoa sẽ mang đến cho du khách những cảm giác thú vị khi mỗi sáng thức dậy được ngắm những ngọn núi xanh mướt và những thửa ruộng bậc thang thơ mộng qua những khung cửa sổ của khách sạn. ', 'http://holidaysapa.com/?lang=vi', '707000', '2017-10-14', '3', '3', '1', '1', '22.3324490', '103.8414230', '2017-10-15 00:57:57', null, 'ks8.jpg', null);
