INSERT INTO `hotels` VALUES ('28', 'Khách sạn Sao Phương Bắc', '24 Ngũ Chỉ Sơn, Sa Pa, Lào Cai', '0214.3.866 789', 'Tựa lưng vào dãy núi Hàm Rồng và quay mặt ra mặt hồ Xuân Viên thơ mộng, khách sạn Sao Phương Bắc Sa Pa với hơn 50 phòng nghỉ được thiết kế theo tiêu chuẩn 3 sao sẽ mang lại cho quý khách những giây phút thư giãn và sảng khoái nhất trong chuyến đi của mình.', 'http://saophuongbachotel.com/', null, '2017-10-14', '3', '3', '1', '1', '22.3368988', '103.8432541', '2017-10-15 00:59:52', '5', 'ks9.jpg', null);
