INSERT INTO `hotels` VALUES ('94', 'Khách sạn Song Ngọc', '2B Hùng Vương, phường 7, Thành phố Cà Mau, Cà Mau', 'null', 'Khách sạn Song NGọc bắt đầu đi vào hoạt động từ tháng 7 năm 2006, đây là một toà nhà cao 6 tầng, hình hộp nằm ở trung tâm, ngay vòng xoay tựơng đài thành phố Cà Mau. Khách sạn Song Ngọc có tất cả 35 phòng, được xếp thành 4 loại, mỗi loại có bảng giá riêng để phục vụ theo yêu cầu của khách. Các phòng được trang bị đầy đủ những vật dụng cần dùng rất tiên nghi và hiện đại như: tủ lạnh, tivi, vòi nước nóng, bồn tắm và nhiều vật dụng khác.. Khách sạn cò thang máy dẫn lên các tầng lầu an toàn và tiên lợi để phục vụ khách. đến với Song Ngọc, Quý khách sẽ được phụ vụ tận tình và chu đáo.', 'null', null, null, '2', '10', '1', '1', '9.1740789', '105.1473312', '2017-09-22 23:28:28', null, 'nhahang7.jpg', null);
INSERT INTO `hotels` VALUES ('95', 'Khách Sạn Hải Châu', '229 Hùng Vương phường 7 thành phố Cà Mau', 'null', 'Khách sạn Hải Châu hiện có 72 phòng đạt tiêu chuẩn quốc tế 2 sao, tất cả các phòng đều được trang bị đầy đủ, tiện nghi sang trọng và hiện đại.', 'null', null, null, '2', '10', '1', '1', '9.1748066', '105.1477585', '2017-09-22 23:24:40', null, 'ks7.jpg', null);
