INSERT INTO `restaurants` VALUES ('21', 'Khu Đồ Nướng Sapa', 'Phố Cầu Mây, Sa Pa, Lào Cai', '', '', '3', 'banhxeo.jpg', 'Ở thị trấn Sapa, đồ nướng được bày bán gần như ở khắp mọi nơi, từ chợ đến trước cổng nhà thờ, thậm chí là cả những con hẻm nhỏ. Chỉ một chiếc bàn con con, dăm ba cái ghế nhựa và một bếp than hồng cũng đã đủ để gọi là “quán nướng”.', '1', '0.0000000', '0.0000000', '2017-10-13 00:09:50', '3', null);
INSERT INTO `restaurants` VALUES ('22', 'Nhà hàng Lepho', '018 Đường Phạm Xuân Huân, Sa Pa, Lào Cai', '(+84-20)873439', '', '3', 'nhahang.jpg', 'Nhà hàng này tọa lạc ở 1 nơi yên tĩnh của trung tâm thị trấn.  Phía bên phải là đường lên Núi Hàm Rồng. Đây là nhà hàng duy nhất phục vụ hai món ăn mang đậm hương vị Việt Nam: Phở và Nem tại Sa Pa.', '1', '22.3349419', '103.8397903', '2017-09-30 22:00:22', '0', null);
INSERT INTO `restaurants` VALUES ('23', 'Nhà hàng Anh Dũng', '69 Xuân Viên, Thị trấn Sa Pa. Huyện Sapa. Lào Cai', '', '', '3', 'nemnuong.jpg', 'Quán Anh Dũng này nằm trên phố Xuân Viên, gần bến xe, rất đông khách. Đồ ăn ở đây khá ngon, giá cả lại phù hợp nên quán có vẻ đông khách hơn các quán xung quanh.', '1', '22.3362942', '103.8416595', '2017-09-30 22:00:23', '0', null);
INSERT INTO `restaurants` VALUES ('24', 'Nhà hàng Xưa và Nay', 'Số 031 – Tổ 7 – Phố Cầu Mây – Thị trấn Sapa – Tỉnh Lào Cai', '', '', '3', 'banhxeo.jpg', 'Xưa và Nay có khuân viên cao ráo, rộng rãi và thoáng mát. Nhà hàng có hai tầng, chuyên phục vụ các món ăn địa phương, Á và Âu với diện tích chứa đến 180 chỗ ngồi. ', '1', '22.3337593', '103.8401108', '2017-09-30 22:00:23', '0', null);
INSERT INTO `restaurants` VALUES ('25', 'Nhà hàng Chapa', '40 Cầu Mây, Sapa', '020 3871245', '', '3', 'nhahang2.jpg', 'Chapa là nhà hàng ở Sapa đầu tiên mở ra cho khách du lịch. Chất lượng món ăn rất tốt và rất đặc trưng của Sa Pa.', '1', '22.2497177', '103.9586182', '2017-09-30 22:00:24', '0', null);
INSERT INTO `restaurants` VALUES ('26', 'Nhà hàng thắng cố A Quỳnh', 'số 15 Thạch Sơn thị trấn Sapa', '02 03 871 555', '', '3', 'vitnauchao.jpg', 'Đây là nhà hàng nổi tiếng nhất với món thắng cố- Một trong những món ăn đặc sản độc đáo của các đồng bào Tây Bắc và đây cũng là món ăn mà du khách không nên bỏ qua trong hành trình du lịch Sapa của mình.', '1', '22.3360367', '103.8419876', '2017-09-30 22:00:24', '0', null);
INSERT INTO `restaurants` VALUES ('27', 'Nhà hàng Red Dao', '4B Thác Bạc', '020 387 2927', '', '3', 'nhahang6.jpg', 'Không gian bên trong của nhà hàng được trang trí bởi màu đỏ đúng như tên gọi của nhà hàng nên nếu bạn đến đây vào những ngày lạnh thì bạn sẽ thấy ấm áp vô cùng. Nhà hàng có đội ngũ nhân viên phục vụ chu đáo, nhiệt tình nên khi tới đây bạn sẽ thấy vô cùng ', '1', '22.3362160', '103.8375931', '2017-09-30 22:00:25', '0', null);
INSERT INTO `restaurants` VALUES ('28', 'Sapa Lotus', '34 Cầu Mây', '020 628 5789', '', '3', 'banhbeo.jpg', 'Đây là nhà hàng chuyên phục vụ các món ăn nướng lẩu truyền thống của người Sapa. Khi đến đây bạn không chỉ được thưởng thức những món ăn ngon mà bạn còn có cơ hội hiểu thêm về văn hóa ẩm thực Sapa.', '1', '22.3363609', '103.8415985', '2017-09-30 22:00:26', '0', null);
